import { PrismaClient } from '@prisma/client';
import { createId } from '@paralleldrive/cuid2';
import bcrypt from 'bcryptjs';

/**
 * User data transformation
 * SQLite user verisini PostgreSQL user'ƒ±na d√∂n√º≈üt√ºr√ºr
 */
function transformUser(sqliteUser) {
  return {
    id: createId(), // Yeni CUID ID
    name: sqliteUser.username,
    username: sqliteUser.username,
    email: sqliteUser.email,
    password: sqliteUser.password_hash, // Hash'li password korunur
    role: sqliteUser.role || 'user',
    status: sqliteUser.is_active ? 'active' : 'suspended',
    bio: sqliteUser.bio || null,
    isActive: sqliteUser.is_active,
    createdAt: new Date(sqliteUser.created_at),
    updatedAt: new Date(sqliteUser.updated_at),
    lastActive: sqliteUser.last_login ? new Date(sqliteUser.last_login) : new Date(),
    lastLogin: sqliteUser.last_login ? new Date(sqliteUser.last_login) : null,
  };
}

/**
 * Users migration
 * SQLite users tablosunu PostgreSQL'e ta≈üƒ±r
 */
async function migrateUsers(sourceDB, targetDB) {
  console.log('üë• Users migration ba≈ülatƒ±lƒ±yor...');
  
  try {
    // Source database'den kullanƒ±cƒ±larƒ± al
    const sqliteUsers = await sourceDB.prepare('SELECT * FROM users').all();
    console.log(`üìä ${sqliteUsers.results.length} kullanƒ±cƒ± bulundu`);

    // ID mapping olu≈ütur (eski ID ‚Üí yeni ID)
    const userIdMapping = new Map();

    // Batch processing
    const batchSize = 50;
    for (let i = 0; i < sqliteUsers.results.length; i += batchSize) {
      const batch = sqliteUsers.results.slice(i, i + batchSize);
      
      const transformedUsers = batch.map(user => {
        const transformed = transformUser(user);
        userIdMapping.set(user.id, transformed.id);
        return transformed;
      });

      // PostgreSQL'e yaz
      await targetDB.user.createMany({
        data: transformedUsers,
        skipDuplicates: true,
      });

      console.log(`‚úÖ Batch ${Math.floor(i/batchSize) + 1}/${Math.ceil(sqliteUsers.results.length/batchSize)} tamamlandƒ±`);
    }

    console.log(`‚úÖ ${sqliteUsers.results.length} kullanƒ±cƒ± ba≈üarƒ±yla migrate edildi`);
    return userIdMapping;
  } catch (error) {
    console.error('‚ùå Users migration hatasƒ±:', error);
    throw error;
  }
}

/**
 * Sessions migration
 * SQLite sessions tablosunu PostgreSQL'e ta≈üƒ±r
 */
async function migrateSessions(sourceDB, targetDB, userIdMapping) {
