/**
 * User validation
 * Migrated kullanƒ±cƒ±larƒ± doƒürular
 */
async function validateUsers(sourceDB, targetDB, userIdMapping) {
  console.log('üîç User validation ba≈ülatƒ±lƒ±yor...');
  
  try {
    // Source count
    const sourceCount = await sourceDB.prepare('SELECT COUNT(*) as count FROM users').first();
    
    // Target count
    const targetCount = await targetDB.user.count();
    
    console.log(`üìä Source users: ${sourceCount.count}`);
    console.log(`üìä Target users: ${targetCount}`);
    
    if (sourceCount.count !== targetCount) {
      throw new Error(`User count mismatch: source=${sourceCount.count}, target=${targetCount}`);
    }

    // ID mapping validation
    const orphanedSessions = await targetDB.session.findMany({
      where: {
        userId: {
          notIn: Array.from(userIdMapping.values())
        }
      }
    });

    if (orphanedSessions.length > 0) {
      console.warn(`‚ö†Ô∏è ${orphanedSessions.length} orphaned session bulundu`);
    }

    console.log('‚úÖ User validation ba≈üarƒ±lƒ±');
    return true;
  } catch (error) {
    console.error('‚ùå User validation hatasƒ±:', error);
    throw error;
  }
}

/**
 * Complete user migration
