 */
async function migrateAllUsers(sourceDB, targetDB) {
  console.log('üöÄ Complete user migration ba≈ülatƒ±lƒ±yor...');
  
  try {
    // Users migration
    const userIdMapping = await migrateUsers(sourceDB, targetDB);
    
    // Sessions migration
    await migrateSessions(sourceDB, targetDB, userIdMapping);
    
    // Validation
    await validateUsers(sourceDB, targetDB, userIdMapping);
    
    // Migration √∂zetini kaydet
    const migrationSummary = {
      timestamp: new Date().toISOString(),
      type: 'users',
      sourceCount: await sourceDB.prepare('SELECT COUNT(*) as count FROM users').first(),
      targetCount: await targetDB.user.count(),
      status: 'completed',
      userIdMapping: Object.fromEntries(userIdMapping)
    };
    
    console.log('üìã Migration √∂zeti:', migrationSummary);
    return migrationSummary;
  } catch (error) {
    console.error('‚ùå Complete user migration hatasƒ±:', error);
    throw error;
  }
}

