 */
function getStatusEmoji(coverage) {
  if (coverage >= COVERAGE_TARGETS.CRITICAL_PATHS_COVERAGE) {
    return '‚úÖ';
  }
  if (coverage >= COVERAGE_TARGETS.MINIMUM_COVERAGE) {
    return '‚ö†Ô∏è';
  }
  return '‚ùå';
}

/**
 * Coverage durumu kontrol et
 * @param {number} coverage - Coverage y√ºzdesi
 * @param {number} target - Hedef coverage
 * @param {string} category - Kategori adƒ±
 * @returns {string} Durum mesajƒ±
 */
function checkCoverageStatus(coverage, target, category) {
  if (coverage >= target) {
    return `‚úÖ ${category} coverage hedefine ula≈ütƒ± (${coverage}% >= ${target}%)`;
  }
  return `‚ùå ${category} coverage hedefin altƒ±nda (${coverage}% < ${target}%)`;
}

/**
