/**
 * Kritik path coverage analizini yap
 * @param {object} frontendCoverage - Frontend coverage verileri
 * @param {object} backendCoverage - Backend coverage verileri
 * @returns {number} Kritik path coverage y√ºzdesi
 */
function analyzeCriticalPaths(frontendCoverage, backendCoverage) {
  // Bu fonksiyon kritik path'lerin coverage'ini analiz eder
  // Ger√ßek uygulamada test dosyalarƒ±nƒ± analiz ederek daha doƒüru sonu√ßlar elde edilir
  
  const criticalPaths = [
    'src/components/GameCard.jsx',
    'src/components/AddGameModal.jsx',
    'src/pages/HomePage.jsx',
    'src/pages/LibraryPage.jsx',
    'src/pages/SettingsPage.jsx',
    'src/services/api.js',
    'src/services/userLibrary.js',
    'backend/src/routes/games.js',
    'backend/src/routes/library.js',
    'backend/src/routes/users.js',
    'backend/src/middleware/auth.js',
  ];
  
  // Basitle≈ütirme: kritik path'lerin ortalama coverage'ini hesapla
  const averageCriticalCoverage = (frontendCoverage.unit + backendCoverage.unit) / 2;
  
  return averageCriticalCoverage;
}

/**
 * Coverage √∂nerileri olu≈ütur
 * @param {object} frontendCoverage - Frontend coverage verileri
 * @param {object} backendCoverage - Backend coverage verileri
 * @param {number} overallCoverage - Genel coverage
 * @param {number} criticalPathsCoverage - Kritik path coverage
 * @returns {string} √ñneriler
 */
function generateRecommendations(frontendCoverage, backendCoverage, overallCoverage, criticalPathsCoverage) {
  const recommendations = [];
  
  // Genel coverage √∂nerileri
  if (overallCoverage < COVERAGE_TARGETS.MINIMUM_COVERAGE) {
    recommendations.push('üéØ Test coverage hedefine ula≈ümak i√ßin daha fazla test yazƒ±n');
    recommendations.push('üìù T√ºm kritik pathlerin test coverageini artƒ±rƒ±n');
  }
  
  // Unit test √∂nerileri
  if (frontendCoverage.unit < COVERAGE_TARGETS.UNIT_TESTS_COVERAGE) {
    recommendations.push('üß™ Frontend unit test coverageini artƒ±rƒ±n');
  }
  
  if (backendCoverage.unit < COVERAGE_TARGETS.UNIT_TESTS_COVERAGE) {
    recommendations.push('üîß Backend unit test coverageini artƒ±rƒ±n');
  }
  
  // Integration test √∂nerileri
  if (frontendCoverage.integration < COVERAGE_TARGETS.INTEGRATION_TESTS_COVERAGE) {
    recommendations.push('üîó Frontend integration test coverageini artƒ±rƒ±n');
  }
  
  if (backendCoverage.integration < COVERAGE_TARGETS.INTEGRATION_TESTS_COVERAGE) {
    recommendations.push('üîß Backend integration test coverageini artƒ±rƒ±n');
  }
  
  // E2E test √∂nerileri
  if (frontendCoverage.e2e < COVERAGE_TARGETS.E2E_TESTS_COVERAGE) {
    recommendations.push('üé≠ Frontend E2E test coverageini artƒ±rƒ±n');
  }
  
  // Kritik path √∂nerileri
  if (criticalPathsCoverage < COVERAGE_TARGETS.CRITICAL_PATHS_COVERAGE) {
    recommendations.push('‚ö° Kritik pathlerin test coverageini artƒ±rƒ±n');
    recommendations.push('üìã √ñzellikle GameCard, AddGameModal, HomePage gibi componentlerin testini yazƒ±n');
  }
  
  // Spesifik √∂neriler
  if (frontendCoverage.total < 50) {
    recommendations.push('üìà Frontend coverage √ßok d√º≈ü√ºk, test stratejisini g√∂zden ge√ßirin');
  }
  
  if (backendCoverage.total < 50) {
    recommendations.push('üìà Backend coverage √ßok d√º≈ü√ºk, test stratejisini g√∂zden ge√ßirin');
  }
  
  return recommendations.join('\n');
}

/**
 * Coverage detaylarƒ±nƒ± olu≈ütur
 * @param {object} frontendCoverage - Frontend coverage verileri
 * @param {object} backendCoverage - Backend coverage verileri
 * @param {number} overallCoverage - Genel coverage
 * @returns {string} Coverage detaylarƒ±
 */
function generateCoverageDetails(frontendCoverage, backendCoverage, overallCoverage) {
  const details = [];
  
  details.push(`üì± Frontend Coverage: ${frontendCoverage.total.toFixed(1)}%`);
  details.push(`üîß Backend Coverage: ${backendCoverage.total.toFixed(1)}%`);
  details.push(`üìà Genel Coverage: ${overallCoverage.toFixed(1)}%`);
  
  // Kategori bazƒ±nda detaylar
  details.push('\nüìä Kategori Bazƒ±nda:');
  details.push(`  Unit Tests: ${frontendCoverage.unit.toFixed(1)}% (Frontend) / ${backendCoverage.unit.toFixed(1)}% (Backend)`);
  details.push(`  Integration Tests: ${frontendCoverage.integration.toFixed(1)}% (Frontend) / ${backendCoverage.integration.toFixed(1)}% (Backend)`);
  details.push(`  E2E Tests: ${frontendCoverage.e2e.toFixed(1)}% (Frontend) / ${backendCoverage.e2e.toFixed(1)}% (Backend)`);
  
  // Durum analizi
  if (overallCoverage >= COVERAGE_TARGETS.MINIMUM_COVERAGE) {
    details.push('\n‚úÖ Coverage hedeflerine ula≈üƒ±ldƒ±!');
  } else {
    details.push('\n‚ùå Coverage hedeflerin altƒ±nda kalƒ±ndƒ±!');
  }
  
  return details.join('\n');
}

// Script'i √ßalƒ±≈ütƒ±r
analyzeCoverage();

export { analyzeCoverage };

import { Routes, Route, Navigate } from "react-router-dom";
import { Suspense, lazy } from "react";
import { AuthProvider, useAuth } from "./contexts/AuthContext";
import { NavigationProvider } from "./contexts/NavigationContext";
import { ActiveSessionProvider } from "./contexts/ActiveSessionContext";
import TutorialOverlay from "./components/Tutorial/TutorialOverlay";
import ElementSelector from "./components/Tutorial/ElementSelector";
import HomePage from "./pages/HomePage";
import FAQPage from "./pages/FAQPage";

// Lazy load pages for better code splitting
const ArkadeDashboard = lazy(() => import("./pages/ArkadeDashboard"));
const ArkadeLibrary = lazy(() => import("./pages/ArkadeLibrary"));
const ArkadeActiveSession = lazy(() => import("./pages/ArkadeActiveSession"));
const BacklogPage = lazy(() => import("./pages/BacklogPage"));
const StatsPage = lazy(() => import("./pages/StatsPage"));
const WishlistPage = lazy(() => import("./pages/WishlistPage"));
const GalleryPage = lazy(() => import("./pages/GalleryPage"));
const LoginPage = lazy(() => import("./pages/LoginPage"));
const SettingsPage = lazy(() => import("./pages/SettingsPage"));
const TutorialAdmin = lazy(() => import("./components/Tutorial/TutorialAdmin"));
const UpdatesAdmin = lazy(() => import("./components/Updates/UpdatesAdmin"));

// Protected Route Component
function ProtectedRoute({ children }) {
  const { isAuthenticated, isLoading } = useAuth();

  if (isLoading) {
    return (
      <div className="min-h-screen bg-gradient-to-br from-gray-900 via-blue-900 to-purple-900 flex items-center justify-center">
        <div className="text-white text-xl">Y√ºkleniyor...</div>
      </div>
    );
  }

  return isAuthenticated() ? children : <Navigate to="/login" replace />;
}

function AdminRoute({ children }) {
  const { user, isLoading } = useAuth();

  if (isLoading) {
    return (
      <div className="min-h-screen bg-gray-900 flex items-center justify-center">
        <div className="text-white">Y√ºkleniyor...</div>
      </div>
    );
  }

  if (!user) {
    return <Navigate to="/login" replace />;
  }

  if (user.role !== "admin") {
    return (
      <div className="min-h-screen bg-gray-900 flex items-center justify-center">
        <div className="text-white text-center">
          <h2 className="text-2xl font-bold mb-4">Eri≈üim Reddedildi</h2>
          <p className="text-gray-400 mb-4">
            Bu sayfaya eri≈üim i√ßin admin yetkisi gereklidir.
          </p>
          <button
            onClick={() => window.history.back()}
            className="bg-blue-600 hover:bg-blue-700 px-4 py-2 rounded"
          >
            Geri D√∂n
          </button>
        </div>
      </div>
    );
  }

  return children;
}

// App Routes Component
function AppRoutes() {
  const { isAuthenticated } = useAuth();

  return (
    <Routes>
      <Route
        path="/login"
        element={
          isAuthenticated() ? <Navigate to="/" replace /> : <LoginPage />
        }
      />
      <Route
        path="/"
        element={
          <ProtectedRoute>
            <HomePage />
          </ProtectedRoute>
        }
      />
      <Route
        path="/arkade"
        element={
          <ProtectedRoute>
            <ArkadeDashboard />
          </ProtectedRoute>
        }
      />

      <Route
        path="/arkade/library"
        element={
          <ProtectedRoute>
            <ArkadeLibrary />
          </ProtectedRoute>
        }
      />
      <Route
        path="/arkade/session"
        element={
          <ProtectedRoute>
            <ArkadeActiveSession />
          </ProtectedRoute>
        }
      />
      <Route
        path="/backlog"
        element={
          <ProtectedRoute>
            <BacklogPage />
          </ProtectedRoute>
        }
      />
      <Route
        path="/stats"
        element={
          <ProtectedRoute>
            <StatsPage />
          </ProtectedRoute>
        }
      />
      <Route
        path="/wishlist"
        element={
          <ProtectedRoute>
            <WishlistPage />
          </ProtectedRoute>
        }
      />
      <Route
        path="/gallery"
        element={
          <ProtectedRoute>
            <GalleryPage />
          </ProtectedRoute>
        }
      />
      <Route
        path="/settings"
        element={
          <ProtectedRoute>
            <SettingsPage />
          </ProtectedRoute>
        }
      />
      <Route
        path="/faq"
        element={
          <ProtectedRoute>
            <FAQPage />
          </ProtectedRoute>
        }
      />
      <Route
        path="/admin/tutorials"
        element={
          <AdminRoute>
            <TutorialAdmin />
          </AdminRoute>
        }
      />
      <Route
        path="/admin/updates"
        element={
          <AdminRoute>
            <UpdatesAdmin />
          </AdminRoute>
        }
      />
    </Routes>
  );
}

function App() {
  return (
    <AuthProvider>
      <NavigationProvider>
        <ActiveSessionProvider>
          <div className="App">
            <AppRoutes />
            <TutorialOverlay />
            <ElementSelector />
          </div>
        </ActiveSessionProvider>
      </NavigationProvider>
    </AuthProvider>
  );
}

export default App;

import React, { useState, useEffect, useMemo, useRef } from "react";
import { createPortal } from "react-dom";
import igdbApi from "../services/igdbApi";
import steamApi from "../services/steamApi";
import cacheService from "../services/cacheService";
import userLibrary from "../services/userLibrary";
import userPreferences from "../services/userPreferences";
import {
  sortGamesByRelevance,
  formatRelevanceScore,
} from "../utils/searchUtils";
import GameSearch from "./AddGameModal/GameSearch";
import GameDetails from "./AddGameModal/GameDetails";
import DLCSelection from "./AddGameModal/DLCSelection";
import GameForm from "./AddGameModal/GameForm";
import CampaignManagement from "./AddGameModal/CampaignManagement";

/**
 * AddGameModal Component - Oyun ekleme/d√ºzenleme modal'ƒ±
 * @param {Object} props - Component props
 * @param {boolean} props.isOpen - Modal a√ßƒ±k mƒ±
 * @param {Function} props.onClose - Modal kapatma fonksiyonu
 * @param {Function} props.onGameAdded - Oyun eklendi callback'i
 * @param {Function} props.onEditGame - Oyun d√ºzenlendi callback'i
 * @param {boolean} props.editMode - D√ºzenleme modu mu
 * @param {Object} props.editGame - D√ºzenlenecek oyun
 */
const AddGameModal = ({
  isOpen,
  onClose,
  onGameAdded,
  onEditGame,
  editMode = false,
  editGame = null,
}) => {
  const [searchTerm, setSearchTerm] = useState("");
  const [searchResults, setSearchResults] = useState([]);
  const [isSearching, setIsSearching] = useState(false);
  const [selectedGame, setSelectedGame] = useState(null);
  const [isAddingGame, setIsAddingGame] = useState(false);
  const [error, setError] = useState("");
  const [searchTimeout, setSearchTimeout] = useState(null);

  // Platform ve durum i√ßin state'ler
  const [platform, setPlatform] = useState("");
  const [platforms, setPlatforms] = useState([]);
  const [gameStatus, setGameStatus] = useState("backlog");
  const [totalPlaytime, setTotalPlaytime] = useState("");

  // Edition/DLC se√ßimi i√ßin state'ler
  const [showVariants, setShowVariants] = useState(false);
  const [gameVariants, setGameVariants] = useState({
    dlcs: [],
    expansions: [],
    editions: [],
  });
  const [isLoadingVariants, setIsLoadingVariants] = useState(false);
  const [selectedVariant, setSelectedVariant] = useState(null);
  const [selectedDlcs, setSelectedDlcs] = useState([]);
  const [steamDlcs, setSteamDlcs] = useState([]);

  // Campaign modu i√ßin state'ler
  const [isCampaignMode, setIsCampaignMode] = useState(false);
  const [campaigns, setCampaigns] = useState([]);

  // Modal konumu i√ßin ref
  const modalRef = useRef(null);

  /**
   * Diƒüer platformlar i√ßin varsayƒ±lan liste
   */
  const defaultOtherPlatforms = [
    "GOG",
    "Nintendo Switch",
    "Origin",
    "Ubisoft Connect",
    "Battle.net",
    "Microsoft Store",
    "Mac App Store",
    "Itch.io",
    "Fiziksel Kopya",
    "Diƒüer",
  ];

  /**
   * Diƒüer platformlar listesi
   */
  const otherPlatforms = useMemo(() => {
    const igdbNames = Array.isArray(platforms)
      ? platforms.map((p) => p?.name).filter(Boolean)
      : [];
    const merged = Array.from(
      new Set([...igdbNames, ...defaultOtherPlatforms]),
    );
    return merged;
  }, [platforms]);

  /**
   * Platform listesini y√ºkler
   */
  const loadPlatforms = async () => {
    try {
      const platformData = await igdbApi.getPlatforms();
      const uniqueNames = Array.from(
        new Set((platformData || []).map((p) => p?.name).filter(Boolean)),
      );
      const normalized = uniqueNames.map((name) => ({ name }));
      setPlatforms(normalized);
    } catch (error) {
      console.error("Platform y√ºkleme hatasƒ±:", error);
      const fallback = defaultOtherPlatforms.map((name) => ({ name }));
      setPlatforms(fallback);
    }
  };

  /**
   * Modal kapandƒ±ƒüƒ±nda temizlik yapar
   */
  const handleModalClose = () => {
    setSearchTerm("");
    setSearchResults([]);
    setSelectedGame(null);
    setError("");
    setGameStatus("backlog");
    setPlatform("");
    setTotalPlaytime("");
    setShowVariants(false);
    setGameVariants({ dlcs: [], expansions: [], editions: [] });
    setSelectedVariant(null);
    setSelectedDlcs([]);
    setSteamDlcs([]);
    setIsCampaignMode(false);
    setCampaigns([]);

    if (searchTimeout) {
      clearTimeout(searchTimeout);
      setSearchTimeout(null);
    }

    onClose();
  };

  /**
   * Oyunu k√ºt√ºphaneye ekler
   */
  const handleAddGame = async () => {
    if (!selectedGame || !platform) {
      setError("L√ºtfen platform se√ßin.");
      return;
    }

    // Campaign zorunluluƒüu kontrol√º
    if (isCampaignMode && campaigns.length === 0) {
      setError(
        'Oyunu k√ºt√ºphaneye eklemek i√ßin en az 1 campaign eklemelisiniz. Campaign eklemek i√ßin "üéØ Campaign Y√∂netimi" butonuna tƒ±klayƒ±n.',
      );
      return;
    }

    setIsAddingGame(true);
    setError("");

    try {
      // Se√ßilen variant varsa onu kullan, yoksa ana oyunu kullan
      const gameToAdd = selectedVariant || selectedGame;

      // Se√ßili platform ve durum bilgilerini ekle
      gameToAdd.status = gameStatus;
      gameToAdd.platform = platform;
      gameToAdd.totalPlaytime = totalPlaytime;

      // Campaign bilgilerini ekle
      if (campaigns.length > 0) {
        gameToAdd.campaigns = campaigns;
      }

      // DLC ve versiyon bilgilerini ekle
      if (selectedDlcs.length > 0) {
        gameToAdd.selectedDlcs = selectedDlcs;
      }

      if (
        gameVariants &&
        (gameVariants.dlcs?.length > 0 ||
          gameVariants.expansions?.length > 0 ||
          gameVariants.editions?.length > 0)
      ) {
        gameToAdd.gameVariants = gameVariants;
      }

      if (steamDlcs.length > 0) {
        gameToAdd.steamDlcs = steamDlcs;
      }

      // Kullanƒ±cƒ± tercihlerini kaydet
      const formData = {
        platform: platform,
        status: gameStatus,
        includeDLCs: selectedDlcs.length > 0,
        selectedDLCs: selectedDlcs,
        selectedCampaigns: campaigns,
        version: selectedVariant ? selectedVariant.name : "",
      };

      userPreferences.saveGameFormPreferences(
        gameToAdd.id,
        gameToAdd.name,
        formData,
      );

      // Callback'i √ßaƒüƒ±r
      if (editMode && onEditGame) {
        onEditGame(gameToAdd);
      } else if (onGameAdded) {
        onGameAdded(gameToAdd);
      }

      handleModalClose();
    } catch (error) {
      setError("Oyun eklenirken hata olu≈ütu: " + error.message);
    } finally {
      setIsAddingGame(false);
    }
  };

  /**
   * Campaign moduna ge√ßi≈ü
   */
  const handleCampaignMode = () => {
    setIsCampaignMode(true);
  };

  /**
   * Oyun ekleme moduna ge√ßi≈ü
   */
  const handleBackToGameMode = () => {
    setIsCampaignMode(false);
  };

  // Effect'ler
  useEffect(() => {
    if (isOpen) {
      loadPlatforms();
    }
  }, [isOpen]);

  useEffect(() => {
    if (isOpen) {
      if (editMode && editGame) {
        // D√ºzenleme modu i√ßin verileri y√ºkle
        const gamePrefs = userPreferences.getGameFormPreferences(editGame.id);
        setSelectedGame({
          id: editGame.id,
          name: editGame.title,
          cover: editGame.coverUrl
            ? { image_id: editGame.coverUrl.split("/").pop().split(".")[0] }
            : null,
          platforms: editGame.platform ? [{ name: editGame.platform }] : [],
          genres: editGame.genre ? [{ name: editGame.genre }] : [],
          first_release_date: editGame.releaseYear
            ? new Date(editGame.releaseYear, 0, 1).getTime() / 1000
            : null,
          rating:
            editGame.rating !== "N/A" ? parseFloat(editGame.rating) * 10 : null,
        });
        setSearchTerm(editGame.title || "");
        setPlatform(gamePrefs.platform || editGame.platform || "");
        setGameStatus(gamePrefs.status || editGame.status || "backlog");
        setTotalPlaytime(editGame.totalPlaytime || editGame.playtime || "");
        setCampaigns(gamePrefs.selectedCampaigns || editGame.campaigns || []);
        setSelectedDlcs(gamePrefs.selectedDLCs || editGame.selectedDlcs || []);

        if (editGame.gameVariants) setGameVariants(editGame.gameVariants);
        if (editGame.steamDlcs) setSteamDlcs(editGame.steamDlcs);

        const hasVariants =
          editGame.gameVariants &&
          (editGame.gameVariants.dlcs?.length || 0) +
            (editGame.gameVariants.expansions?.length || 0) +
            (editGame.gameVariants.editions?.length || 0) >
          0;
        const hasSteamDlcs =
          editGame.steamDlcs && editGame.steamDlcs.length > 0;
        if (hasVariants || hasSteamDlcs) {
          setShowVariants(true);
        }
      } else {
        // Ekleme modu i√ßin genel tercihler
        const generalPrefs = userPreferences.getGameFormPreferences();
        setPlatform(generalPrefs.platform || "");
        setGameStatus(generalPrefs.status || "backlog");
        setSelectedDlcs(generalPrefs.selectedDLCs || []);
        setCampaigns(generalPrefs.selectedCampaigns || []);
      }
    }
  }, [isOpen, editMode, editGame]);

  // Modal a√ßƒ±k deƒüilse render etme
  if (!isOpen) return null;

  return createPortal(
    <div className="fixed inset-0 bg-black/80 backdrop-blur-sm z-[99999] flex items-center justify-center p-4" data-ers="add-game-modal.overlay">
      <div className="relative w-full max-w-4xl mx-auto" data-ers="add-game-modal.container">
        <div
          ref={modalRef}
          className="bg-gradient-to-br from-[#1a0f2e] to-[#0a0e27] rounded-2xl border border-white/20 w-full max-w-4xl max-h-[90vh] overflow-hidden"
          data-ers="add-game-modal.content"
        >
          {/* Header */}
          <div className="flex items-center justify-between p-6 border-b border-white/10" data-ers="add-game-modal.header">
            <div className="flex items-center gap-4">
              {isCampaignMode && (
                <button
                  onClick={handleBackToGameMode}
                  className="w-8 h-8 bg-white/10 hover:bg-white/20 rounded-lg flex items-center justify-center transition-colors"
                  data-ers="add-game-modal.back-to-game"
                >
                  ‚Üê
                </button>
              )}
              <h2 className="text-2xl font-bold bg-gradient-to-r from-[#00ff88] to-[#00d4ff] bg-clip-text text-transparent" data-ers="add-game-modal.title">
                {isCampaignMode
                  ? "Campaign y√∂netimi"
                  : editMode
                    ? "Oyun D√ºzenle"
                    : "Oyun Ekle"}
              </h2>
            </div>
            <button
              onClick={handleModalClose}
              className="w-8 h-8 bg-white/10 hover:bg-white/20 rounded-lg flex items-center justify-center transition-colors"
              data-ers="add-game-modal.close-button"
            >
              ‚úï
            </button>
          </div>

          <div className="p-6 overflow-y-auto max-h-[calc(90vh-120px)]" data-ers="add-game-modal.body">
            {isCampaignMode ? (
              // Campaign Modu
              <CampaignManagement
                campaigns={campaigns}
                setCampaigns={setCampaigns}
                selectedGame={selectedGame}
                onBack={handleBackToGameMode}
              />
            ) : !selectedGame ? (
              // Arama Ekranƒ±
              <GameSearch
                searchTerm={searchTerm}
                setSearchTerm={setSearchTerm}
                searchResults={searchResults}
                setSearchResults={setSearchResults}
                isSearching={isSearching}
                setIsSearching={setIsSearching}
                error={error}
                setError={setError}
                onGameSelect={setSelectedGame}
                searchTimeout={searchTimeout}
                setSearchTimeout={setSearchTimeout}
              />
            ) : (
              // Oyun Detaylarƒ± ve Ekleme Ekranƒ±
              <div className="space-y-6" data-ers="add-game-modal.game-details-section">
                <div className="flex items-center gap-4">
                  {!editMode && (
                    <button
                      onClick={() => setSelectedGame(null)}
                      className="flex items-center gap-2 text-gray-400 hover:text-white transition-colors"
                      data-ers="add-game-modal.back-to-search"
                    >
                      ‚Üê Geri d√∂n
                    </button>
                  )}
                </div>

                <div className="grid grid-cols-1 lg:grid-cols-3 gap-6" data-ers="add-game-modal.game-content">
                  {/* Sol: Oyun G√∂rseli ve Bilgileri */}
                  <GameDetails
                    selectedGame={selectedGame}
                    gameVariants={gameVariants}
                    showVariants={showVariants}
                    isLoadingVariants={isLoadingVariants}
                    selectedVariant={selectedVariant}
                    setSelectedVariant={setSelectedVariant}
                  />

                  {/* Saƒü: Oyun Formu */}
                  <div className="lg:col-span-2" data-ers="add-game-modal.form-section">
                    <GameForm
                      selectedGame={selectedGame}
                      platform={platform}
                      setPlatform={setPlatform}
                      gameStatus={gameStatus}
                      setGameStatus={setGameStatus}
                      totalPlaytime={totalPlaytime}
                      setTotalPlaytime={setTotalPlaytime}
                      otherPlatforms={otherPlatforms}
                      showVariants={showVariants}
                      gameVariants={gameVariants}
                      isLoadingVariants={isLoadingVariants}
                      selectedDlcs={selectedDlcs}
                      setSelectedDlcs={setSelectedDlcs}
                      steamDlcs={steamDlcs}
                      isCampaignMode={isCampaignMode}
                      campaigns={campaigns}
                      setCampaigns={setCampaigns}
                      handleCampaignMode={handleCampaignMode}
                      error={error}
                      setError={setError}
                      isAddingGame={isAddingGame}
                      handleAddGame={handleAddGame}
                      editMode={editMode}
                    />
                  </div>
                </div>

                {/* DLC Se√ßimi - Tam Geni≈ülik */}
                {showVariants && !isLoadingVariants && (
                  <div className="lg:col-span-3" data-ers="add-game-modal.dlc-section">
                    <DLCSelection
                      gameVariants={gameVariants}
                      selectedDlcs={selectedDlcs}
                      setSelectedDlcs={setSelectedDlcs}
                      steamDlcs={steamDlcs}
                    />
                  </div>
                )}
              </div>
            )}
          </div>
        </div>
      </div>
    </div>,
    document.body,
  );
};

