                      )}
                      {steamCount > 0 && (
                        <span className="text-green-300">
                          <span className="inline-block w-2 h-2 bg-green-500 rounded-full mr-1"></span>
                          Steam: {steamCount}
                        </span>
                      )}
                    </div>
                  </div>
                </div>
              )
            );
          })()}
        </>
      )}
    </div>
  );
};

export default DLCSelection;

import React, { useState, useCallback } from "react";
import igdbApi from "../../services/igdbApi";
import userLibrary from "../../services/userLibrary";
import { sortGamesByRelevance, formatRelevanceScore } from "../../utils/searchUtils";
import { getImageUrl } from "./utils";

/**
 * GameSearch Component - Oyun arama ve sonu√ßlarƒ± g√∂sterme
 * @param {Object} props - Component props
 * @param {Function} props.onGameSelect - Oyun se√ßildiƒüinde √ßaƒürƒ±lƒ±r
 * @param {Function} props.onEditGame - Oyun d√ºzenlendiƒüinde √ßaƒürƒ±lƒ±r
 * @param {boolean} props.isSearching - Arama durumu
 * @param {Function} props.setIsSearching - Arama durumunu ayarlar
 * @param {string} props.searchTerm - Arama terimi
 * @param {Function} props.setSearchTerm - Arama terimini ayarlar
 * @param {Array} props.searchResults - Arama sonu√ßlarƒ±
 * @param {Function} props.setSearchResults - Arama sonu√ßlarƒ±nƒ± ayarlar
 * @param {string} props.error - Hata mesajƒ±
 * @param {Function} props.setError - Hata mesajƒ±nƒ± ayarlar
 */
const GameSearch = ({
  onGameSelect,
  onEditGame,
  isSearching,
  setIsSearching,
  searchTerm,
  setSearchTerm,
  searchResults,
  setSearchResults,
  error,
  setError,
}) => {
  const [searchTimeout, setSearchTimeout] = useState(null);

  /**
   * Oyunun k√ºt√ºphanede olup olmadƒ±ƒüƒ±nƒ± kontrol eder
   * @param {string} gameId - Oyun ID'si
   * @returns {Promise<boolean>} - K√ºt√ºphanede var mƒ±?
   */
  const checkGameInLibrary = async (gameId) => {
    try {
      const lib = await userLibrary.getUserLibrary();
      return (
        Array.isArray(lib.entries) &&
        lib.entries.some((entry) => entry.gameId === gameId.toString())
      );
    } catch (error) {
      console.error("K√ºt√ºphane kontrol√º hatasƒ±:", error);
      return false;
    }
  };

  /**
   * Debounced arama fonksiyonu
   * @param {string} term - Arama terimi
   */
  const handleSearchDebounced = useCallback((term) => {
    // √ñnceki timeout'u temizle
    if (searchTimeout) {
      clearTimeout(searchTimeout);
    }

    // Yeni timeout ayarla
    const newTimeout = setTimeout(() => {
      performSearch(term);
    }, 500); // 500ms bekle

    setSearchTimeout(newTimeout);
  }, [searchTimeout]);

  /**
   * Ger√ßek arama fonksiyonu
   * @param {string} term - Arama terimi
   */
  const performSearch = async (term) => {
    if (!term.trim()) {
      setSearchResults([]);
      return;
    }

    setIsSearching(true);
    setError("");

    try {
      const results = await igdbApi.searchGames(term, 20); // Daha fazla sonu√ß al

      // Relevance score'a g√∂re sƒ±rala
      const sortedResults = sortGamesByRelevance(results, term);

      // Her oyun i√ßin k√ºt√ºphane durumunu kontrol et
      const resultsWithLibraryStatus = await Promise.all(
        sortedResults.map(async (game) => ({
          ...game,
          isInLibrary: await checkGameInLibrary(game.id),
        })),
      );

      setSearchResults(resultsWithLibraryStatus);

      if (resultsWithLibraryStatus.length === 0) {
        setError("Arama sonucu bulunamadƒ±. Farklƒ± bir terim deneyin.");
      }
    } catch (error) {
      setError("Arama sƒ±rasƒ±nda hata olu≈ütu: " + error.message);
      setSearchResults([]);
    } finally {
      setIsSearching(false);
    }
  };

  /**
   * Manuel arama (Enter tu≈üu veya buton)
   */
  const handleSearch = () => {
    if (searchTimeout) {
      clearTimeout(searchTimeout);
    }
    performSearch(searchTerm);
  };

  /**
   * Enter tu≈üu ile arama
   * @param {Object} e - Keyboard event
   */
  const handleKeyPress = (e) => {
    if (e.key === "Enter") {
      handleSearch();
    }
  };

  /**
   * Arama terimi deƒüi≈ütiƒüinde √ßaƒürƒ±lƒ±r
   * @param {Object} e - Input event
   */
  const handleSearchTermChange = (e) => {
    const value = e.target.value;
    setSearchTerm(value);
    // Otomatik arama (debounced)
    if (value.length >= 2) {
      handleSearchDebounced(value);
    } else if (value.length === 0) {
      setSearchResults([]);
      setError("");
    }
  };

  return (
    <div className="space-y-6" data-ers="add-game-modal.search">
      {/* Arama Kutusu */}
      <div className="space-y-4">
        <div className="flex gap-3">
          <input
            type="text"
            value={searchTerm}
            onChange={handleSearchTermChange}
            onKeyPress={handleKeyPress}
            placeholder="Oyun adƒ± girin... (otomatik arama)"
            className="flex-1 px-4 py-3 bg-white/5 border border-white/20 rounded-lg text-white placeholder-gray-400 focus:outline-none focus:border-[#00ff88]/50"
            data-ers="add-game-modal.search-input"
          />
          <button
            onClick={handleSearch}
            disabled={isSearching || !searchTerm.trim()}
            className="px-6 py-3 bg-gradient-to-r from-[#00ff88] to-[#00d4ff] text-black font-bold rounded-lg hover:scale-105 transition-transform disabled:opacity-50 disabled:cursor-not-allowed"
            data-ers="add-game-modal.search-button"
          >
            {isSearching ? "üîç Arƒ±yor..." : "üîç Ara"}
          </button>
        </div>

        {error && (
          <div
            className="p-3 bg-red-500/20 border border-red-500/30 rounded-lg text-red-300 text-sm"
            data-ers="add-game-modal.search-error"
          >
            {error}
          </div>
        )}
      </div>

      {/* Arama Sonu√ßlarƒ± */}
      {searchResults.length > 0 && (
        <div className="space-y-4" data-ers="add-game-modal.search-results">
          <h3 className="text-lg font-bold text-white">Arama Sonu√ßlarƒ±</h3>
          <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-4 max-h-96 overflow-y-auto">
            {searchResults.map((game, index) => (
              <div
                key={game.id}
                className="bg-white/5 rounded-lg border border-white/10 hover:border-[#00ff88]/50 transition-all group relative"
                data-ers={`add-game-modal.search-result.${index + 1}`}
              >
                {/* K√ºt√ºphane Durumu Badge */}
                {game.isInLibrary && (
                  <div className="absolute top-2 right-2 z-10">
                    <span className="bg-[#00ff88] text-black text-xs font-bold px-2 py-1 rounded-full">
                      ‚úì K√ºt√ºphanede
                    </span>
                  </div>
                )}

                <div
                  onClick={() =>
                    !game.isInLibrary && onGameSelect(game)
                  }
                  className={`${
                    !game.isInLibrary ? "cursor-pointer" : "cursor-default"
                  }`}
                  data-ers={`add-game-modal.search-result-card.${index + 1}`}
                >
                  <div className="aspect-[2/3] overflow-hidden rounded-t-lg">
                    <img
                      src={getImageUrl(game.cover, "cover_big")}
                      alt={game.name}
                      className={`w-full h-full object-cover transition-transform ${
                        !game.isInLibrary ? "group-hover:scale-105" : ""
                      }`}
                    />
                  </div>
                  <div className="p-3">
                    <h4
                      className={`font-bold text-sm truncate transition-colors ${
                        !game.isInLibrary
                          ? "text-white group-hover:text-[#00ff88]"
                          : "text-gray-300"
                      }`}
                    >
                      {game.name}
                    </h4>
                    <p className="text-xs text-gray-400 mt-1">
                      {game.first_release_date
                        ? new Date(
                            game.first_release_date * 1000,
                          ).getFullYear()
                        : "Bilinmiyor"}
                    </p>

                    {/* Relevance Score G√∂stergesi */}
                    {game.relevanceScore && (
                      <div className="flex items-center gap-1 mt-2">
                        <span className="text-xs text-[#00ff88]">üéØ</span>
                        <span
                          className={`text-xs font-medium ${formatRelevanceScore(
                            game.relevanceScore,
                          ).color}`}
                        >
                          %{Math.round(game.relevanceScore)}
                        </span>
                        <span className="text-xs text-gray-500">
                          {formatRelevanceScore(game.relevanceScore).label}
                        </span>
                      </div>
                    )}

                    {game.rating && (
                      <div className="flex items-center gap-1 mt-1">
                        <span className="text-xs text-yellow-400">‚≠ê</span>
                        <span className="text-xs text-gray-300">
                          {Math.round(game.rating)}/100
                        </span>
                      </div>
                    )}
                  </div>
                </div>

                {/* D√ºzenle Butonu - Sadece k√ºt√ºphanede olan oyunlar i√ßin */}
                {game.isInLibrary && (
                  <div className="p-3 pt-0">
                    <button
                      onClick={(e) => {
                        e.stopPropagation();
                        if (onEditGame) {
                          onEditGame(game.id);
                        }
                      }}
                      className="w-full bg-blue-500/20 hover:bg-blue-500/30 text-blue-400 border border-blue-500/30 hover:border-blue-500/50 px-3 py-2 rounded-lg text-sm font-medium transition-all flex items-center justify-center gap-2"
                      data-ers={`add-game-modal.edit-button.${index + 1}`}
                    >
                      <span>‚úèÔ∏è</span>
                      D√ºzenle
                    </button>
                  </div>
                )}
              </div>
            ))}
          </div>
        </div>
      )}
    </div>
  );
};

export default GameSearch;

import { useNavigate, useLocation } from "react-router-dom";
import { useState, useRef, useEffect } from "react";
import { createPortal } from "react-dom";
import ReactMarkdown from "react-markdown";
import { useAuth } from "../contexts/AuthContext";
import { useNavigation } from "../contexts/NavigationContext";
import BackButton from "./BackButton";

function ArkadeHeader() {
  const navigate = useNavigate();
  const location = useLocation();
  const { user, logout } = useAuth();
  const { currentPage } = useNavigation();
  const [isProfileDropdownOpen, setIsProfileDropdownOpen] = useState(false);
  const [isNotificationDropdownOpen, setIsNotificationDropdownOpen] =
    useState(false);
  const [dropdownPosition, setDropdownPosition] = useState({
    top: 0,
    right: 0,
  });
  const [notificationDropdownPosition, setNotificationDropdownPosition] =
    useState({ top: 0, right: 0 });
  const [selectedNotification, setSelectedNotification] = useState(null);
  const [isNotificationDetailOpen, setIsNotificationDetailOpen] =
    useState(false);
  const [notifications, setNotifications] = useState([]);
  const dropdownRef = useRef(null);
  const buttonRef = useRef(null);
  const notificationDropdownRef = useRef(null);
  const notificationButtonRef = useRef(null);

  // Bildirimleri localStorage'dan y√ºkle
  useEffect(() => {
    const loadNotifications = () => {
      try {
        const storedNotifications = JSON.parse(
          localStorage.getItem("notifications") || "[]",
        );
        const userReadStatus = JSON.parse(
          localStorage.getItem(`notificationReadStatus_${user?.id}`) || "{}",
        );

        // Bildirimleri kullanƒ±cƒ± bazlƒ± okunma durumu ile birle≈ütir
        const notificationsWithReadStatus = storedNotifications.map(
          (notification) => ({
            ...notification,
            read: userReadStatus[notification.id] || false,
            time: formatNotificationTime(notification.timestamp),
          }),
        );

        setNotifications(notificationsWithReadStatus);
      } catch (error) {
        console.error("Bildirimler y√ºklenirken hata:", error);
        setNotifications([]);
      }
    };

    if (user?.id) {
      loadNotifications();

      // localStorage deƒüi≈üikliklerini dinle
      const handleStorageChange = (e) => {
        if (e.key === "notifications") {
          loadNotifications();
        }
      };

      window.addEventListener("storage", handleStorageChange);

      return () => {
        window.removeEventListener("storage", handleStorageChange);
      };
    }
  }, [user?.id]);

  // Zaman formatƒ± helper fonksiyonu
  const formatNotificationTime = (timestamp) => {
    if (!timestamp) return "Bilinmeyen zaman";

    const now = new Date();
    const notificationTime = new Date(timestamp);
    const diffInMinutes = Math.floor((now - notificationTime) / (1000 * 60));

    if (diffInMinutes < 1) return "Az √∂nce";
    if (diffInMinutes < 60) return `${diffInMinutes} dakika √∂nce`;

    const diffInHours = Math.floor(diffInMinutes / 60);
    if (diffInHours < 24) return `${diffInHours} saat √∂nce`;

    const diffInDays = Math.floor(diffInHours / 24);
    if (diffInDays < 7) return `${diffInDays} g√ºn √∂nce`;

    return notificationTime.toLocaleDateString("tr-TR");
  };

  // Dropdown pozisyonunu hesapla
  const updateDropdownPosition = () => {
    if (buttonRef.current) {
      const rect = buttonRef.current.getBoundingClientRect();
      setDropdownPosition({
        top: rect.bottom + 8,
        right: window.innerWidth - rect.right,
      });
    }
  };

  // Bildirim dropdown pozisyonunu hesapla
  const updateNotificationDropdownPosition = () => {
    if (notificationButtonRef.current) {
      const rect = notificationButtonRef.current.getBoundingClientRect();
      setNotificationDropdownPosition({
        top: rect.bottom + 8,
        right: window.innerWidth - rect.right,
      });
    }
  };

  // Dropdown dƒ±≈üƒ±na tƒ±klandƒ±ƒüƒ±nda kapat
  useEffect(() => {
    function handleClickOutside(event) {
      if (
        dropdownRef.current &&
        !dropdownRef.current.contains(event.target) &&
        buttonRef.current &&
        !buttonRef.current.contains(event.target)
      ) {
        setIsProfileDropdownOpen(false);
      }
      if (
        notificationDropdownRef.current &&
        !notificationDropdownRef.current.contains(event.target) &&
        notificationButtonRef.current &&
        !notificationButtonRef.current.contains(event.target)
      ) {
        setIsNotificationDropdownOpen(false);
      }
    }

    document.addEventListener("mousedown", handleClickOutside);
    return () => {
      document.removeEventListener("mousedown", handleClickOutside);
    };
  }, []);

  // Bildirim detay g√∂r√ºnt√ºleme
  const handleNotificationClick = (notification) => {
    setSelectedNotification(notification);
    setIsNotificationDetailOpen(true);
    setIsNotificationDropdownOpen(false);

    // Bildirimi okundu olarak i≈üaretle
    markNotificationAsRead(notification.id);
  };

  // Bildirimi okundu olarak i≈üaretle
  const markNotificationAsRead = (notificationId) => {
    if (!user?.id) return;

    try {
      // Kullanƒ±cƒ± bazlƒ± okunma durumunu g√ºncelle
      const userReadStatus = JSON.parse(
        localStorage.getItem(`notificationReadStatus_${user.id}`) || "{}",
      );
      const readTime = new Date().toISOString();

      userReadStatus[notificationId] = true;
      userReadStatus[`${notificationId}_readAt`] = readTime;

      localStorage.setItem(
        `notificationReadStatus_${user.id}`,
        JSON.stringify(userReadStatus),
      );

      // State'i g√ºncelle
      setNotifications(
        notifications.map((n) =>
          n.id === notificationId ? { ...n, read: true } : n,
        ),
      );
    } catch (error) {
      console.error("Bildirim okunma durumu g√ºncellenirken hata:", error);
    }
  };

  // T√ºm bildirimleri okundu olarak i≈üaretle
  const markAllNotificationsAsRead = () => {
    if (!user?.id) return;

    try {
      // Kullanƒ±cƒ± bazlƒ± okunma durumunu g√ºncelle
      const userReadStatus = JSON.parse(
        localStorage.getItem(`notificationReadStatus_${user.id}`) || "{}",
      );
      const readTime = new Date().toISOString();

      notifications.forEach((notification) => {
        userReadStatus[notification.id] = true;
        userReadStatus[`${notification.id}_readAt`] = readTime;
      });

      localStorage.setItem(
        `notificationReadStatus_${user.id}`,
        JSON.stringify(userReadStatus),
      );

      // State'i g√ºncelle
      setNotifications(notifications.map((n) => ({ ...n, read: true })));
    } catch (error) {
      console.error("T√ºm bildirimler okundu i≈üaretlenirken hata:", error);
    }
  };

  const closeNotificationDetail = () => {
    setIsNotificationDetailOpen(false);
    setSelectedNotification(null);
  };

