import React, { useState, useRef } from "react";
import { Upload, X, Image, Loader2 } from "lucide-react";

const ImageUpload = ({
  onUpload,
  currentImage,
  uploadType = "avatar",
  maxSize = 5,
  acceptedTypes = "image/*",
  className = "",
  disabled = false,
  label = "Resim Y√ºkle",
}) => {
  const [isUploading, setIsUploading] = useState(false);
  const [dragActive, setDragActive] = useState(false);
  const [preview, setPreview] = useState(currentImage);
  const fileInputRef = useRef(null);

  const handleFileSelect = async (file) => {
    if (!file) return;

    // Validate file type
    if (!file.type.startsWith("image/")) {
      alert("L√ºtfen sadece resim dosyasƒ± se√ßin");
      return;
    }

    // Validate file size (MB)
    if (file.size > maxSize * 1024 * 1024) {
      alert(`Dosya boyutu ${maxSize}MB'dan k√º√ß√ºk olmalƒ±dƒ±r`);
      return;
    }

    // Create preview
    const reader = new FileReader();
    reader.onload = (e) => setPreview(e.target.result);
    reader.readAsDataURL(file);

    // Upload file
    setIsUploading(true);
    try {
      const formData = new FormData();
      formData.append(getFieldName(uploadType), file);

      // Add additional fields based on upload type
      if (uploadType === "platform-logo") {
        const platformName = prompt("Platform adƒ±nƒ± girin:");
        if (!platformName) {
          setIsUploading(false);
          return;
        }
        formData.append("platformName", platformName);
      }

      await onUpload(formData);
    } catch (error) {
      console.error("Upload error:", error);
      alert("Y√ºkleme sƒ±rasƒ±nda hata olu≈ütu");
      setPreview(currentImage); // Reset preview on error
    } finally {
      setIsUploading(false);
    }
  };

  const getFieldName = (type) => {
    switch (type) {
      case "avatar":
        return "avatar";
      case "platform-logo":
        return "logo";
      case "game-cover":
        return "cover";
      default:
        return "image";
    }
  };

  const handleDrag = (e) => {
    e.preventDefault();
    e.stopPropagation();
    if (e.type === "dragenter" || e.type === "dragover") {
      setDragActive(true);
    } else if (e.type === "dragleave") {
      setDragActive(false);
    }
  };

  const handleDrop = (e) => {
    e.preventDefault();
    e.stopPropagation();
    setDragActive(false);

    if (disabled || isUploading) return;

    const files = e.dataTransfer.files;
    if (files && files[0]) {
      handleFileSelect(files[0]);
    }
  };

  const handleInputChange = (e) => {
    const file = e.target.files?.[0];
    if (file) {
      handleFileSelect(file);
    }
  };

  const handleClick = () => {
    if (!disabled && !isUploading) {
      fileInputRef.current?.click();
    }
  };

  const handleRemove = () => {
    setPreview(null);
    if (fileInputRef.current) {
      fileInputRef.current.value = "";
    }
  };

  const getUploadAreaSize = () => {
    switch (uploadType) {
      case "avatar":
        return "w-32 h-32";
      case "platform-logo":
        return "w-24 h-24";
      case "game-cover":
        return "w-48 h-64";
      default:
        return "w-32 h-32";
    }
  };

  return (
    <div className={`relative ${className}`}>
      <input
        ref={fileInputRef}
        type="file"
        accept={acceptedTypes}
        onChange={handleInputChange}
        className="hidden"
        disabled={disabled || isUploading}
      />

      <div
        className={`
          ${getUploadAreaSize()}
          border-2 border-dashed rounded-lg cursor-pointer
          transition-all duration-200 relative overflow-hidden
          ${
            dragActive
              ? "border-blue-500 bg-blue-50"
              : "border-gray-300 hover:border-gray-400"
          }
          ${disabled || isUploading ? "opacity-50 cursor-not-allowed" : ""}
        `}
        onDragEnter={handleDrag}
        onDragLeave={handleDrag}
        onDragOver={handleDrag}
        onDrop={handleDrop}
        onClick={handleClick}
      >
        {preview ? (
          <div className="relative w-full h-full">
            <img
              src={preview}
              alt="Preview"
              className="w-full h-full object-cover rounded-lg"
            />
            {!disabled && !isUploading && (
              <button
                onClick={(e) => {
                  e.stopPropagation();
                  handleRemove();
                }}
                className="absolute top-1 right-1 bg-red-500 text-white rounded-full p-1 hover:bg-red-600 transition-colors"
              >
                <X size={12} />
              </button>
            )}
            {isUploading && (
              <div className="absolute inset-0 bg-black bg-opacity-50 flex items-center justify-center rounded-lg">
                <Loader2 className="animate-spin text-white" size={24} />
              </div>
            )}
          </div>
        ) : (
          <div className="flex flex-col items-center justify-center h-full text-gray-500">
            {isUploading ? (
              <Loader2 className="animate-spin mb-2" size={24} />
            ) : (
              <Upload className="mb-2" size={24} />
            )}
            <span className="text-xs text-center px-2">
              {isUploading ? "Y√ºkleniyor..." : label}
            </span>
          </div>
        )}
      </div>

      <div className="mt-2 text-xs text-gray-500 text-center">
        Max {maxSize}MB ‚Ä¢ JPG, PNG, GIF, WebP
      </div>
    </div>
  );
};

export default ImageUpload;

import { useNavigate } from "react-router-dom";
import { useState, useRef, useEffect } from "react";
import { createPortal } from "react-dom";
import { useAuth } from "../contexts/AuthContext";
import { useTutorial, useTutorialAdmin } from "../hooks/useTutorial";

function Header() {
  const navigate = useNavigate();
  const { user, logout } = useAuth();
  const { startTutorial } = useTutorial();
  const { isAdmin } = useTutorialAdmin();
  const [isProfileDropdownOpen, setIsProfileDropdownOpen] = useState(false);
  const [isNotificationDropdownOpen, setIsNotificationDropdownOpen] =
    useState(false);
  const [isTutorialDropdownOpen, setIsTutorialDropdownOpen] = useState(false);
  const [dropdownPosition, setDropdownPosition] = useState({
    top: 0,
    right: 0,
  });
  const [notificationDropdownPosition, setNotificationDropdownPosition] =
    useState({ top: 0, right: 0 });
  const [tutorialDropdownPosition, setTutorialDropdownPosition] = useState({
    top: 0,
    right: 0,
  });
  const [notifications, setNotifications] = useState([]);
  const dropdownRef = useRef(null);
  const buttonRef = useRef(null);
  const notificationDropdownRef = useRef(null);
  const notificationButtonRef = useRef(null);
  const tutorialDropdownRef = useRef(null);
  const tutorialButtonRef = useRef(null);

  // Bildirimleri localStorage'dan y√ºkle
  useEffect(() => {
    const loadNotifications = () => {
      try {
        const storedNotifications = JSON.parse(
          localStorage.getItem("notifications") || "[]",
        );
        const userReadStatus = JSON.parse(
          localStorage.getItem(`notificationReadStatus_${user?.id}`) || "{}",
        );

        // Bildirimleri kullanƒ±cƒ± bazlƒ± okunma durumu ile birle≈ütir
        const notificationsWithReadStatus = storedNotifications.map(
          (notification) => ({
            ...notification,
            read: userReadStatus[notification.id] || false,
            time: formatNotificationTime(notification.timestamp),
          }),
        );

        setNotifications(notificationsWithReadStatus);
      } catch (error) {
        console.error("Bildirimler y√ºklenirken hata:", error);
        setNotifications([]);
      }
    };

    if (user?.id) {
      loadNotifications();

      // localStorage deƒüi≈üikliklerini dinle
      const handleStorageChange = (e) => {
        if (e.key === "notifications") {
          loadNotifications();
        }
      };

      window.addEventListener("storage", handleStorageChange);

      return () => {
        window.removeEventListener("storage", handleStorageChange);
      };
    }
  }, [user?.id]);

  // Zaman formatƒ± helper fonksiyonu
  const formatNotificationTime = (timestamp) => {
    if (!timestamp) return "Bilinmeyen zaman";

    const now = new Date();
    const notificationTime = new Date(timestamp);
    const diffInMinutes = Math.floor((now - notificationTime) / (1000 * 60));

    if (diffInMinutes < 1) return "Az √∂nce";
    if (diffInMinutes < 60) return `${diffInMinutes} dakika √∂nce`;

    const diffInHours = Math.floor(diffInMinutes / 60);
    if (diffInHours < 24) return `${diffInHours} saat √∂nce`;

    const diffInDays = Math.floor(diffInHours / 24);
    if (diffInDays < 7) return `${diffInDays} g√ºn √∂nce`;

    return notificationTime.toLocaleDateString("tr-TR");
  };

  // Dropdown pozisyonunu hesapla
  const updateDropdownPosition = () => {
    if (buttonRef.current) {
      const rect = buttonRef.current.getBoundingClientRect();
      setDropdownPosition({
        top: rect.bottom + 8,
        right: window.innerWidth - rect.right,
      });
    }
  };

  // Bildirim dropdown pozisyonunu hesapla
  const updateNotificationDropdownPosition = () => {
    if (notificationButtonRef.current) {
      const rect = notificationButtonRef.current.getBoundingClientRect();
      setNotificationDropdownPosition({
        top: rect.bottom + 8,
        right: window.innerWidth - rect.right,
      });
    }
  };

  // Tutorial dropdown pozisyonunu hesapla
  const updateTutorialDropdownPosition = () => {
    if (tutorialButtonRef.current) {
      const rect = tutorialButtonRef.current.getBoundingClientRect();
      setTutorialDropdownPosition({
        top: rect.bottom + 8,
        right: window.innerWidth - rect.right,
      });
    }
  };

  // Dropdown dƒ±≈üƒ±na tƒ±klandƒ±ƒüƒ±nda kapat
  useEffect(() => {
    function handleClickOutside(event) {
      if (
        dropdownRef.current &&
        !dropdownRef.current.contains(event.target) &&
        buttonRef.current &&
        !buttonRef.current.contains(event.target)
      ) {
        setIsProfileDropdownOpen(false);
      }
      if (
        notificationDropdownRef.current &&
        !notificationDropdownRef.current.contains(event.target) &&
        notificationButtonRef.current &&
        !notificationButtonRef.current.contains(event.target)
      ) {
        setIsNotificationDropdownOpen(false);
      }
      if (
        tutorialDropdownRef.current &&
        !tutorialDropdownRef.current.contains(event.target) &&
        tutorialButtonRef.current &&
        !tutorialButtonRef.current.contains(event.target)
      ) {
        setIsTutorialDropdownOpen(false);
      }
    }

    document.addEventListener("mousedown", handleClickOutside);
    return () => {
      document.removeEventListener("mousedown", handleClickOutside);
    };
  }, []);

  // Bildirimi okundu olarak i≈üaretle
  const markAsRead = (notificationId) => {
    try {
      const userReadStatus = JSON.parse(
        localStorage.getItem(`notificationReadStatus_${user?.id}`) || "{}",
      );
      userReadStatus[notificationId] = true;
      localStorage.setItem(
        `notificationReadStatus_${user?.id}`,
        JSON.stringify(userReadStatus),
      );

      // State'i g√ºncelle
      setNotifications((prev) =>
        prev.map((n) => (n.id === notificationId ? { ...n, read: true } : n)),
      );
    } catch (error) {
      console.error("Bildirim okundu olarak i≈üaretlenirken hata:", error);
    }
  };

  return (
    <header className="h-20 bg-gradient-to-r from-[#1a1a2e]/90 via-[#2d1b69]/90 to-[#1a1a2e]/90 backdrop-blur-xl border-b border-purple-500/20 flex items-center justify-between px-8">
      {/* Logo ve Brand */}
      <div
        className="flex items-center gap-4 cursor-pointer hover:opacity-80 transition-opacity"
        onClick={() => navigate("/")}
      >
        <div className="w-12 h-12 rounded-xl bg-gradient-to-br from-purple-500 to-pink-500 flex items-center justify-center shadow-lg shadow-purple-500/25">
          <span className="text-2xl font-bold text-white">J</span>
        </div>
        <div>
          <h1 className="text-2xl font-bold bg-gradient-to-r from-white to-gray-300 bg-clip-text text-transparent">
            Jun-Oro
          </h1>
          <p className="text-sm text-purple-300">Gaming Dashboard</p>
        </div>
      </div>

      {/* Bo≈ü alan - arama √ßubuƒüu kaldƒ±rƒ±ldƒ± */}
      <div className="flex-1"></div>

      {/* User Profile & Actions */}
      <div className="flex items-center gap-4">
        {/* Tutorial / Help Button */}
        <div className="relative z-[10000]">
          <button
            id="header-tutorial-btn"
            data-registry="H3.3.1"
            ref={tutorialButtonRef}
            onClick={() => {
              // '?' ikonuna tƒ±klandƒ±ƒüƒ±nda doƒürudan tutorial'ƒ± ba≈ülat
              try {
                startTutorial();
              } finally {
                setIsTutorialDropdownOpen(false);
              }
            }}
            className="relative p-2 rounded-lg bg-white/10 hover:bg-white/20 transition-colors"
            title="Tutorial'ƒ± Ba≈ülat"
          >
            <span className="text-lg">‚ùì</span>
          </button>
        </div>

        {/* Notifications */}
        <div className="relative z-[10000]">
          <button
            ref={notificationButtonRef}
            onClick={() => {
              updateNotificationDropdownPosition();
              setIsNotificationDropdownOpen(!isNotificationDropdownOpen);
            }}
            className="relative p-2 rounded-lg bg-white/10 hover:bg-white/20 transition-colors"
          >
            <span className="text-lg">üîî</span>
            {notifications.some((n) => !n.read) && (
              <div className="absolute -top-1 -right-1 w-3 h-3 bg-red-500 rounded-full"></div>
            )}
          </button>

          {/* Notification Dropdown */}
          {isNotificationDropdownOpen &&
            createPortal(
              <div
                ref={notificationDropdownRef}
                className="fixed bg-gray-800/95 backdrop-blur-xl border border-gray-700/50 rounded-xl shadow-2xl w-80 max-h-96 overflow-hidden z-[10000]"
                style={{
                  top: `${notificationDropdownPosition.top}px`,
                  right: `${notificationDropdownPosition.right}px`,
                }}
              >
                <div className="p-4 border-b border-gray-700/50">
                  <h3 className="text-white font-semibold">Bildirimler</h3>
                  {notifications.some((n) => !n.read) && (
                    <p className="text-purple-400 text-xs mt-1">
                      {notifications.filter((n) => !n.read).length} okunmamƒ±≈ü
                      bildirim
                    </p>
                  )}
                </div>

                <div className="overflow-y-auto max-h-80">
                  {notifications.length > 0 ? (
                    notifications.map((notification) => (
                      <div
                        key={notification.id}
                        onClick={() => markAsRead(notification.id)}
                        className={`p-3 border-b border-gray-700/30 hover:bg-gray-700/30 cursor-pointer transition-colors ${
                          !notification.read ? "bg-purple-500/10" : ""
                        }`}
                      >
                        <div className="flex items-start gap-3">
                          <div className="text-lg">{notification.icon}</div>
                          <div className="flex-1 min-w-0">
                            <div className="flex items-center gap-2">
                              <h4 className="text-white text-sm font-medium truncate">
                                {notification.title}
                              </h4>
                              {!notification.read && (
                                <div className="w-2 h-2 bg-purple-500 rounded-full flex-shrink-0"></div>
                              )}
                            </div>
                            <p className="text-gray-400 text-xs mt-1 line-clamp-2">
                              {notification.message}
                            </p>
                            <p className="text-gray-500 text-xs mt-1">
                              {notification.time}
                            </p>
                          </div>
                        </div>
                      </div>
                    ))
                  ) : (
                    <div className="p-8 text-center">
                      <div className="text-4xl mb-2">üîî</div>
                      <p className="text-gray-400 text-sm">
                        Hen√ºz bildirim yok
                      </p>
                    </div>
                  )}
                </div>
              </div>,
              document.body,
            )}
        </div>

        {/* User Profile */}
        <div className="relative z-[10000]">
          <button
            ref={buttonRef}
            onClick={() => {
              updateDropdownPosition();
              setIsProfileDropdownOpen(!isProfileDropdownOpen);
            }}
            className="flex items-center gap-3 bg-white/10 rounded-xl px-4 py-2 hover:bg-white/15 transition-colors cursor-pointer"
          >
            <div className="w-8 h-8 rounded-full overflow-hidden bg-gradient-to-br from-purple-500 to-pink-500 flex items-center justify-center shadow-lg">
              {user?.profileImage ? (
                <img
                  src={user.profileImage}
                  alt="Profil"
                  className="w-full h-full object-cover"
                />
              ) : (
                <span className="text-white font-bold text-sm">
                  {user?.avatar || user?.username?.[0]?.toUpperCase() || "U"}
                </span>
              )}
            </div>
            <div className="text-left">
              <div className="text-white font-medium text-sm">
                {user?.username}
              </div>
              <div className="text-purple-400 text-xs flex items-center gap-1">
                <div className="w-2 h-2 bg-purple-400 rounded-full"></div>
                {user?.role === "admin" ? "Admin" : "Kullanƒ±cƒ±"}
              </div>
            </div>
            <span
              className={`text-gray-400 text-sm transition-transform ${isProfileDropdownOpen ? "rotate-180" : ""}`}
            >
              ‚ñº
            </span>
          </button>

          {/* Dropdown Menu */}
          {isProfileDropdownOpen &&
            createPortal(
              <div
                ref={dropdownRef}
                className="fixed w-64 bg-gray-800/95 backdrop-blur-xl border border-gray-700/50 rounded-xl shadow-2xl z-[99999]"
                style={{
                  top: `${dropdownPosition.top}px`,
                  right: `${dropdownPosition.right}px`,
                }}
              >
                <div className="p-4 border-b border-gray-700/50">
                  <div className="flex items-center gap-3">
                    <div className="w-12 h-12 rounded-full overflow-hidden bg-gradient-to-br from-purple-500 to-pink-500 flex items-center justify-center shadow-lg">
                      {user?.profileImage ? (
                        <img
                          src={user.profileImage}
                          alt="Profil"
                          className="w-full h-full object-cover"
                        />
                      ) : (
                        <span className="text-white font-bold">
                          {user?.avatar ||
                            user?.username?.[0]?.toUpperCase() ||
                            "U"}
                        </span>
                      )}
                    </div>
                    <div>
                      <div className="text-white font-medium">
                        {user?.username}
                      </div>
                      <div className="text-gray-400 text-sm">
                        {user?.role === "admin"
                          ? "Admin Kullanƒ±cƒ±"
                          : "Standart Kullanƒ±cƒ±"}
                      </div>
                      <div className="text-purple-400 text-xs flex items-center gap-1 mt-1">
                        <div className="w-2 h-2 bg-purple-400 rounded-full"></div>
                        √áevrimi√ßi
                      </div>
                    </div>
                  </div>
                </div>

                <div className="p-2">
                  <button
                    onClick={() => {
                      setIsProfileDropdownOpen(false);
                      alert(
                        "Profil sayfasƒ± hen√ºz hazƒ±r deƒüil. Yakƒ±nda geliyor!",
                      );
                    }}
                    className="w-full flex items-center gap-3 px-3 py-2 rounded-lg hover:bg-gray-700/50 transition-colors text-left"
                  >
                    <span className="text-lg">üë§</span>
                    <span className="text-white">Profil (Yakƒ±nda)</span>
                  </button>

                  <button
                    onClick={() => {
                      navigate("/settings");
                      setIsProfileDropdownOpen(false);
                    }}
                    className="w-full flex items-center gap-3 px-3 py-2 rounded-lg hover:bg-gray-700/50 transition-colors text-left"
                  >
                    <span className="text-lg">‚öôÔ∏è</span>
                    <span className="text-white">Ayarlar</span>
                  </button>

                  <div className="border-t border-gray-700/50 my-2"></div>

                  <button
                    onClick={() => {
                      logout();
                      setIsProfileDropdownOpen(false);
                      navigate("/login");
                    }}
                    className="w-full flex items-center gap-3 px-3 py-2 rounded-lg hover:bg-red-500/10 transition-colors text-left"
                  >
                    <span className="text-lg">üö™</span>
                    <span className="text-red-400">√áƒ±kƒ±≈ü Yap</span>
                  </button>
                </div>
              </div>,
              document.body,
            )}
        </div>
      </div>
    </header>
  );
}

export default Header;

import React, { useState, useEffect, useRef } from "react";
import {
  FaGamepad,
  FaHeart,
  FaGithub,
  FaTwitter,
  FaDiscord,
  FaSteam,
  FaCode,
  FaRocket,
  FaGem,
  FaCoffee,
} from "react-icons/fa";
import elementRegistry from "../../elementRegistry.json";
import { useAuth } from "../contexts/AuthContext";

const SiteFooter = () => {
  const currentYear = new Date().getFullYear();
  const { user } = useAuth();
  const isAdminUser = user?.role === "admin";

  // Debug mode states
  const [isDebugMode, setIsDebugMode] = useState(false);
  const [hoveredElement, setHoveredElement] = useState(null);
  const [selectedElement, setSelectedElement] = useState(null);
  const [showProperties, setShowProperties] = useState(false);
  const [measureMode, setMeasureMode] = useState(false);
  const [measureStart, setMeasureStart] = useState(null);
  const [measureEnd, setMeasureEnd] = useState(null);
  const [resizeMode, setResizeMode] = useState(false);
  const [resizeElement, setResizeElement] = useState(null);
  const [resizeStart, setResizeStart] = useState(null);
  const [isDragging, setIsDragging] = useState(false);
  const [mousePosition, setMousePosition] = useState({ x: 0, y: 0 });
  const overlayRef = useRef(null);
  const propertiesPanelRef = useRef(null);

  // Debug overlay'i olu≈ütur/kaldƒ±r
  useEffect(() => {
    if (isDebugMode && isAdminUser) {
      createDebugOverlay();
    } else {
      removeDebugOverlay();
      setHoveredElement(null);
      setSelectedElement(null);
      setShowProperties(false);
      setMeasureMode(false);
      setResizeMode(false);
      setResizeElement(null);
      setResizeStart(null);
      setIsDragging(false);
    }

    return () => removeDebugOverlay();
  }, [isDebugMode, isAdminUser]);

  const createDebugOverlay = () => {
    if (overlayRef.current) return;

    const overlay = document.createElement("div");
    overlay.id = "debug-overlay";
    overlay.style.cssText = `
      position: fixed;
      top: 0;
      left: 0;
      width: 100vw;
      height: 100vh;
      pointer-events: none;
      z-index: 9999;
      background: transparent;
    `;
    document.body.appendChild(overlay);
    overlayRef.current = overlay;

    // Event listeners ekle
    document.addEventListener("mousemove", handleMouseMove);
    document.addEventListener("click", handleClick);
    document.addEventListener("contextmenu", handleRightClick);
  };

  const removeDebugOverlay = () => {
    if (overlayRef.current) {
      document.body.removeChild(overlayRef.current);
      overlayRef.current = null;
    }

    // Event listeners kaldƒ±r
    document.removeEventListener("mousemove", handleMouseMove);
    document.removeEventListener("click", handleClick);
    document.removeEventListener("contextmenu", handleRightClick);

    // T√ºm debug highlight'larƒ±nƒ± temizle
    document.querySelectorAll(".debug-highlight").forEach((el) => {
      el.classList.remove("debug-highlight");
    });

    // T√ºm debug-selected sƒ±nƒ±flarƒ±nƒ± temizle
    document.querySelectorAll(".debug-selected").forEach((el) => {
      el.classList.remove("debug-selected");
    });
  };

  const handleMouseMove = (e) => {
    if (!isDebugMode) return;

    // Mouse pozisyonunu g√ºncelle
    setMousePosition({ x: e.clientX, y: e.clientY });

    const element = document.elementFromPoint(e.clientX, e.clientY);
    // Tooltip'leri ve debug elementlerini filtrele
    if (
      !element ||
      element.closest("footer") ||
      element.closest("#debug-overlay") ||
      element.style?.position === "fixed" ||
      element.closest('[style*="position: fixed"]')
    )
      return;

    // T√ºm eski highlight'larƒ± temizle (sadece debug-highlight, debug-selected'ƒ± koru)
    document.querySelectorAll(".debug-highlight").forEach((el) => {
      el.classList.remove("debug-highlight");
    });

    // Yeni element'i highlight et (se√ßili element deƒüilse)
    if (element !== selectedElement) {
      element.classList.add("debug-highlight");
    }

    setHoveredElement(element);

    // Measure mode i√ßin
    if (measureMode && measureStart) {
      setMeasureEnd({ x: e.clientX, y: e.clientY });
    }
  };

  const handleClick = (e) => {
    if (!isDebugMode) return;
    e.preventDefault();
    e.stopPropagation();

    const element = document.elementFromPoint(e.clientX, e.clientY);
    // Tooltip'leri ve debug elementlerini filtrele
    if (
      !element ||
      element.closest("footer") ||
      element.closest("#debug-overlay") ||
      element.style?.position === "fixed" ||
      element.closest('[style*="position: fixed"]')
    )
      return;

    if (measureMode) {
      if (!measureStart) {
        setMeasureStart({ x: e.clientX, y: e.clientY });
      } else {
        setMeasureEnd({ x: e.clientX, y: e.clientY });
        setMeasureMode(false);
      }
      return;
    }

    if (resizeMode) {
      setResizeElement(element);
      return;
    }

    // Element se√ß ve √∂zelliklerini g√∂ster
    if (selectedElement === element) {
      // Aynƒ± elemente tekrar tƒ±klandƒ± - se√ßimi kaldƒ±r
      selectedElement.classList.remove("debug-selected");
      setSelectedElement(null);
      setShowProperties(false);
    } else {
      // Farklƒ± element se√ßildi
      if (selectedElement) {
        selectedElement.classList.remove("debug-selected");
      }

      element.classList.add("debug-selected");
      setSelectedElement(element);
      setShowProperties(true);
    }
  };

  const handleRightClick = (e) => {
    if (!isDebugMode) return;
    e.preventDefault();

    const element = document.elementFromPoint(e.clientX, e.clientY);
    if (
      !element ||
      element.closest("footer") ||
      element.closest("#debug-overlay")
    )
      return;

    // Unique ID'yi (√∂ncelik data-registry) kopyala
    const uniqueId = element.getAttribute("data-registry") || element.id || "";

    if (uniqueId) {
      try {
        if (navigator.clipboard && navigator.clipboard.writeText) {
          void navigator.clipboard.writeText(uniqueId).catch(() => {});
        }
      } catch {}
      // Toast g√∂ster (yalnƒ±zca ID)
      showToast(`üÜî ID kopyalandƒ±: ${uniqueId}`);
    } else {
      // ID yoksa bilgi ver
      showToast(
        "‚ùå Bu elementin unique ID'si yok (id veya data-registry bulunamadƒ±)",
      );
    }
  };

  // ERS sisteminden element bilgilerini getir
  const getERSInfo = (element) => {
    if (!element) return null;

    // Element'in ID'si veya data-registry attribute'u ile ERS'de ara
    const elementId = element.id;
    const registryAttr = element.getAttribute("data-registry");

    // ERS'de bu element'i bul
    const ersElement = elementRegistry.elements.find((item) => {
      return (
        item.selector === `#${elementId}` ||
        item.registryId === registryAttr ||
        item.registryId === elementId
      );
    });

    return ersElement || null;
  };

  const getElementName = (element) => {
    let name = element.tagName.toLowerCase();

    if (element.id) {
      name += `#${element.id}`;
    }

    if (element.className) {
      // className string deƒüilse string'e √ßevir
      const classNameStr =
        typeof element.className === "string"
          ? element.className
          : element.className.toString();

      const classes = classNameStr
        .split(" ")
        .filter((c) => c && !c.startsWith("debug-"));
      if (classes.length > 0) {
        name += `.${classes.join(".")}`;
      }
    }

    return name;
  };

  const getElementProperties = (element) => {
    if (!element) return {};

    const computedStyle = window.getComputedStyle(element);
    const rect = element.getBoundingClientRect();
    const ersInfo = getERSInfo(element);

    // className string deƒüilse string'e √ßevir
    const classNameStr = element.className
      ? typeof element.className === "string"
        ? element.className
        : element.className.toString()
      : "";

    return {
      tag: element.tagName.toLowerCase(),
      id: element.id || "yok",
      classes: classNameStr
        .split(" ")
        .filter((c) => c && !c.startsWith("debug-")),
      ers: ersInfo
        ? {
            registryId: ersInfo.registryId,
            label: ersInfo.label,
            component: ersInfo.metadata?.component,
            feature: ersInfo.metadata?.feature,
            type: ersInfo.metadata?.type,
            criticalPath: ersInfo.metadata?.criticalPath,
          }
        : null,
      dimensions: {
        width: Math.round(rect.width),
        height: Math.round(rect.height),
        top: Math.round(rect.top),
        left: Math.round(rect.left),
      },
      styles: {
        display: computedStyle.display,
        position: computedStyle.position,
        backgroundColor: computedStyle.backgroundColor,
        color: computedStyle.color,
        fontSize: computedStyle.fontSize,
        fontFamily: computedStyle.fontFamily,
        margin: computedStyle.margin,
        padding: computedStyle.padding,
        border: computedStyle.border,
        zIndex: computedStyle.zIndex,
      },
    };
  };

  const showToast = (message) => {
    const toast = document.createElement("div");
    toast.textContent = message;
    toast.style.cssText = `
      position: fixed;
      top: 20px;
      right: 20px;
      background: #00ff88;
      color: black;
      padding: 12px 20px;
      border-radius: 8px;
      font-weight: bold;
      z-index: 10000;
      animation: slideIn 0.3s ease;
    `;

    document.body.appendChild(toast);
    setTimeout(() => {
      toast.remove();
    }, 2000);
  };

  const calculateDistance = () => {
    if (!measureStart || !measureEnd) return 0;
    const dx = measureEnd.x - measureStart.x;
    const dy = measureEnd.y - measureStart.y;
    return Math.round(Math.sqrt(dx * dx + dy * dy));
  };

  const properties = selectedElement
    ? getElementProperties(selectedElement)
    : null;

  return (
    <>
      {/* CSS Styles */}
      <style>{`
        .debug-highlight {
          outline: 2px solid #00ff88 !important;
          outline-offset: -2px !important;
          background-color: rgba(0, 255, 136, 0.1) !important;
        }
        
        .debug-selected {
          outline: 3px solid #ff6b6b !important;
          outline-offset: -3px !important;
          background-color: rgba(255, 107, 107, 0.1) !important;
        }

        @keyframes slideIn {
          from { transform: translateX(100%); opacity: 0; }
          to { transform: translateX(0); opacity: 1; }
        }

        .debug-measure-line {
          position: fixed;
          background: #00ff88;
          z-index: 9998;
          pointer-events: none;
        }

        .debug-resize-overlay {
          position: fixed;
          border: 2px dashed #ffd700;
          background: rgba(255, 215, 0, 0.1);
          z-index: 9997;
          pointer-events: none;
        }

        .debug-resize-handle {
          position: absolute;
          width: 8px;
          height: 8px;
          background: #ffd700;
          border: 1px solid #000;
          cursor: nw-resize;
          z-index: 9998;
        }

        .debug-resize-handle.se {
          bottom: -4px;
          right: -4px;
          cursor: se-resize;
        }

        .debug-resize-handle.sw {
          bottom: -4px;
          left: -4px;
          cursor: sw-resize;
        }

        .debug-resize-handle.ne {
          top: -4px;
          right: -4px;
          cursor: ne-resize;
        }

        .debug-resize-handle.nw {
          top: -4px;
          left: -4px;
          cursor: nw-resize;
        }
      `}</style>

      {/* Measure Line */}
      {measureMode && measureStart && measureEnd && (
        <>
          {/* Ger√ßek √ßizgi */}
          <div
            style={{
              position: "fixed",
              left: `${measureStart.x}px`,
              top: `${measureStart.y}px`,
              width: `${Math.sqrt(Math.pow(measureEnd.x - measureStart.x, 2) + Math.pow(measureEnd.y - measureStart.y, 2))}px`,
              height: "2px",
              background: "#00ff88",
              transformOrigin: "0 50%",
              transform: `rotate(${Math.atan2(measureEnd.y - measureStart.y, measureEnd.x - measureStart.x)}rad)`,
              zIndex: 9998,
              pointerEvents: "none",
            }}
          />

          {/* Ba≈ülangƒ±√ß noktasƒ± */}
          <div
            style={{
              position: "fixed",
              left: `${measureStart.x - 4}px`,
              top: `${measureStart.y - 4}px`,
              width: "8px",
              height: "8px",
              background: "#00ff88",
              borderRadius: "50%",
              zIndex: 9999,
              pointerEvents: "none",
            }}
          />

          {/* Biti≈ü noktasƒ± */}
          <div
            style={{
              position: "fixed",
              left: `${measureEnd.x - 4}px`,
              top: `${measureEnd.y - 4}px`,
              width: "8px",
              height: "8px",
              background: "#00ff88",
              borderRadius: "50%",
              zIndex: 9999,
              pointerEvents: "none",
            }}
          />

          {/* Mesafe etiketi */}
          <div
            style={{
              position: "fixed",
              left: `${(measureStart.x + measureEnd.x) / 2}px`,
              top: `${(measureStart.y + measureEnd.y) / 2}px`,
              transform: "translate(-50%, -50%)",
              background: "#00ff88",
              color: "black",
              padding: "4px 8px",
              borderRadius: "4px",
              fontSize: "12px",
              fontWeight: "bold",
              zIndex: 10000,
              pointerEvents: "none",
              boxShadow: "0 2px 8px rgba(0,0,0,0.3)",
            }}
          >
            {calculateDistance()}px
          </div>
        </>
      )}

      {/* Resize Overlay */}
      {resizeMode && resizeElement && (
        <div
          className="debug-resize-overlay"
          style={{
            left: `${resizeElement.getBoundingClientRect().left}px`,
            top: `${resizeElement.getBoundingClientRect().top}px`,
            width: `${resizeElement.getBoundingClientRect().width}px`,
            height: `${resizeElement.getBoundingClientRect().height}px`,
          }}
        >
          <div className="debug-resize-handle nw" />
          <div className="debug-resize-handle ne" />
          <div className="debug-resize-handle sw" />
          <div className="debug-resize-handle se" />
        </div>
      )}

      {/* Element Properties Panel */}
      {showProperties && properties && (
        <div
          ref={propertiesPanelRef}
          style={{
            position: "fixed",
            top: "20px",
            right: "20px",
            width: "350px",
            maxHeight: "80vh",
            background: "rgba(26, 26, 46, 0.95)",
            backdropFilter: "blur(20px)",
            border: "1px solid rgba(0, 255, 136, 0.3)",
            borderRadius: "12px",
            padding: "20px",
            color: "white",
            fontSize: "14px",
            zIndex: 10000,
            overflow: "auto",
          }}
        >
          <div
            style={{
              display: "flex",
              justifyContent: "space-between",
              alignItems: "center",
              marginBottom: "16px",
            }}
          >
