      }
    } catch (e) {
      // sessiz ge√ß
    }
  }, [isAdmin]);

  const loadTutorials = async () => {
    try {
      setIsLoading(true);
      const tutorialList = await listTutorials();
      setTutorials(tutorialList);
    } catch (err) {
      setError("Tutorial listesi y√ºklenirken hata olu≈ütu");
    } finally {
      setIsLoading(false);
    }
  };

  const handleEdit = (tutorialId) => {
    setSelectedTutorialId(tutorialId);
    setEditModalOpen(true);
  };

  const handleCreate = () => {
    setSelectedTutorialId(null);
    setEditModalOpen(true);
  };

  const handleOpenImport = () => {
    setImportModalOpen(true);
  };

  const handleDelete = async (tutorialId) => {
    if (window.confirm("Bu tutorial'ƒ± silmek istediƒüinizden emin misiniz?")) {
      try {
        await deleteTutorial(tutorialId);
        await loadTutorials(); // Listeyi yenile
      } catch (err) {
        setError("Tutorial silinirken hata olu≈ütu");
      }
    }
  };

  const handleModalClose = () => {
    setEditModalOpen(false);
    setSelectedTutorialId(null);
    loadTutorials(); // Listeyi yenile
  };

  const handleImportClose = () => {
    setImportModalOpen(false);
  };

  const handleProceedToEdit = () => {
    // Import modalinden gelen draft, yeni modal a√ßƒ±ldƒ±ƒüƒ±nda otomatik y√ºklenecek
    setSelectedTutorialId(null);
    setImportModalOpen(false);
    setEditModalOpen(true);
  };

  if (!isAdmin) {
    if (embedded) {
      return (
        <div className="bg-slate-800 rounded-lg p-8">
          <h2 className="text-xl font-bold text-white mb-2">Yetkisiz Eri≈üim</h2>
          <p className="text-gray-300 mb-4">
            Bu b√∂l√ºm i√ßin admin yetkisi gereklidir.
          </p>
          <button
            onClick={() => window.history.back()}
            className="bg-purple-600 hover:bg-purple-700 text-white px-4 py-2 rounded-lg transition-colors"
          >
            Geri D√∂n
          </button>
        </div>
      );
    }
    return (
      <div className="min-h-screen bg-slate-900 flex items-center justify-center">
        <div className="bg-slate-800 rounded-lg p-8 max-w-md mx-4">
          <h2 className="text-2xl font-bold text-white mb-4">
            Yetkisiz Eri≈üim
          </h2>
          <p className="text-gray-300 mb-6">
            Bu sayfaya eri≈ümek i√ßin admin yetkisine sahip olmanƒ±z gerekiyor.
          </p>
          <button
            onClick={() => window.history.back()}
            className="w-full bg-purple-600 hover:bg-purple-700 text-white px-4 py-2 rounded-lg transition-colors"
          >
            Geri D√∂n
          </button>
        </div>
      </div>
    );
  }

  const content = (
    <div className="p-6 flex-1">
      <div className="max-w-6xl mx-auto">
        {/* Header */}
        <div className="flex items-center justify-between mb-8">
          <div>
            <h1 className="text-3xl font-bold text-white mb-2">
              Tutorial Y√∂netimi
            </h1>
            <p className="text-gray-400">Uygulama tutorial'larƒ±nƒ± y√∂netin</p>
          </div>
          <div className="flex items-center gap-2">
            <button
              onClick={handleCreate}
              className="bg-purple-600 hover:bg-purple-700 text-white px-6 py-3 rounded-lg transition-colors flex items-center gap-2"
            >
              <span>+</span>
              Yeni Tutorial
            </button>
            <button
              id="tutorial-import-txt"
              data-registry="ADMIN.TUTORIALS.B.IMPORT_TXT"
              onClick={handleOpenImport}
              className="bg-slate-700 hover:bg-slate-600 text-white px-6 py-3 rounded-lg transition-colors flex items-center gap-2 border border-slate-600"
              title=".txt dosyasƒ±ndan i√ße aktar"
            >
              <span>‚¨áÔ∏è</span>
              ƒ∞√ße Aktar (.txt)
            </button>
          </div>
        </div>

        {/* Error Message */}
        {error && (
          <div className="bg-red-600/20 border border-red-600/50 rounded-lg p-4 mb-6">
            <p className="text-red-300">{error}</p>
          </div>
        )}

        {/* Loading */}
        {isLoading ? (
          <div className="text-center py-12">
            <div className="text-white text-lg">Y√ºkleniyor...</div>
          </div>
        ) : (
          /* Tutorial List */
          <div className="grid gap-6">
            {tutorials.length === 0 ? (
              <div className="text-center py-12">
                <div className="text-gray-400 text-lg mb-4">
                  Hen√ºz tutorial bulunmuyor
                </div>
                <button
                  onClick={handleCreate}
                  className="bg-purple-600 hover:bg-purple-700 text-white px-6 py-3 rounded-lg transition-colors"
                >
                  ƒ∞lk Tutorial'ƒ± Olu≈ütur
                </button>
              </div>
            ) : (
              tutorials.map((tutorial) => (
                <div
                  key={tutorial.id}
                  className="bg-slate-800 rounded-lg p-6 border border-slate-700 hover:border-slate-600 transition-colors"
                >
                  <div className="flex items-start justify-between">
                    <div className="flex-1">
                      <div className="flex items-center gap-3 mb-2">
                        <h3 className="text-xl font-semibold text-white">
                          {tutorial.title}
                        </h3>
                        <span className="bg-slate-700 text-gray-300 px-2 py-1 rounded text-sm">
                          {tutorial.id}
                        </span>
                        <span className="bg-purple-600/20 text-purple-300 px-2 py-1 rounded text-sm">
                          v{tutorial.version}
                        </span>
                      </div>

                      {tutorial.description && (
                        <p className="text-gray-400 mb-3">
                          {tutorial.description}
                        </p>
                      )}

                      <div className="flex items-center gap-4 text-sm text-gray-500">
                        <span>{tutorial.steps?.length || 0} adƒ±m</span>
                        <span>‚Ä¢</span>
                        <span>
                          Otomatik ba≈ülat:{" "}
                          {tutorial.settings?.autoStart ? "Evet" : "Hayƒ±r"}
                        </span>
                        <span>‚Ä¢</span>
                        <span>
                          Atlama:{" "}
                          {tutorial.settings?.allowSkip ? "ƒ∞zinli" : "Yasak"}
                        </span>
                      </div>
                    </div>

                    <div className="flex items-center gap-2 ml-4">
                      <button
                        onClick={() => handleEdit(tutorial.id)}
                        className="bg-blue-600 hover:bg-blue-700 text-white px-4 py-2 rounded-lg transition-colors"
                      >
                        D√ºzenle
                      </button>
                      <button
                        onClick={() => handleDelete(tutorial.id)}
                        className="bg-red-600 hover:bg-red-700 text-white px-4 py-2 rounded-lg transition-colors"
                      >
                        Sil
                      </button>
                    </div>
                  </div>

                  {/* Tutorial Steps Preview */}
                  {tutorial.steps && tutorial.steps.length > 0 && (
                    <div className="mt-4 pt-4 border-t border-slate-700">
                      <h4 className="text-sm font-medium text-gray-300 mb-2">
                        Adƒ±mlar:
                      </h4>
                      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-2">
                        {tutorial.steps.slice(0, 6).map((step, index) => (
                          <div
                            key={index}
                            className="bg-slate-700/50 rounded p-2 text-sm"
                          >
                            <div className="text-white font-medium truncate">
                              {index + 1}. {step.title || "Ba≈ülƒ±ksƒ±z"}
                            </div>
                            <div className="text-gray-400 text-xs truncate">
                              Target: {step.target || "Belirtilmemi≈ü"}
                            </div>
                          </div>
                        ))}
                        {tutorial.steps.length > 6 && (
                          <div className="bg-slate-700/30 rounded p-2 text-sm text-gray-400 flex items-center justify-center">
                            +{tutorial.steps.length - 6} daha...
                          </div>
                        )}
                      </div>
                    </div>
                  )}
                </div>
              ))
            )}
          </div>
        )}

        {/* Edit Modal */}
        <TutorialEditModal
          isOpen={editModalOpen}
          onClose={handleModalClose}
          tutorialId={selectedTutorialId}
        />
        {/* Import Modal */}
        <TutorialImportModal
          isOpen={importModalOpen}
          onClose={handleImportClose}
          onProceedToEdit={handleProceedToEdit}
        />
      </div>
    </div>
  );

  if (embedded) {
    return <div className="bg-slate-900/40 rounded-xl">{content}</div>;
  }

  return (
    <div className="min-h-screen bg-slate-900 flex flex-col">
      <Header />
      {content}
      <SiteFooter />
    </div>
  );
}

export default TutorialAdmin;

import { useState, useEffect, useRef } from "react";
import { createPortal } from "react-dom";
import { useTutorialAdmin } from "../../hooks/useTutorial";
import { TUTORIAL_TYPES, PAGE_CATEGORIES } from "../../utils/tutorialTypes";

function TutorialEditModal({ isOpen, onClose, tutorialId = null }) {
  const { isAdmin, saveTutorial, loadTutorial, deleteTutorial, listTutorials } =
    useTutorialAdmin();
  const selectorWindowRef = useRef(null);
  // Draft persistence key: unique per tutorial or 'new'
  const draftKeyRef = useRef(null);
  const [tutorialData, setTutorialData] = useState({
    id: "",
    title: "",
    description: "",
    version: "1.0.0",
    type: TUTORIAL_TYPES.PAGE_GUIDE,
    targetPage: null, // { categoryId, pageId, pageName, pagePath }
    steps: [],
    settings: {
      autoStart: true,
      showProgress: true,
      allowSkip: true,
      overlay: {
        color: "rgba(0, 0, 0, 0.7)",
        blur: true,
      },
      highlight: {
        color: "#8b5cf6",
        borderWidth: 3,
        borderRadius: 8,
        padding: 8,
      },
      modal: {
        maxWidth: 400,
        borderRadius: 12,
        backgroundColor: "rgba(17, 24, 39, 0.95)",
        textColor: "#ffffff",
      },
    },
  });
  const [currentStep, setCurrentStep] = useState(0);
  const [isLoading, setIsLoading] = useState(false);
  const [error, setError] = useState("");
  const [success, setSuccess] = useState("");
  const [draggedStep, setDraggedStep] = useState(null);
  const restoredFromDraftRef = useRef(false);

  // Resolve draft key whenever modal opens or tutorialId changes
  useEffect(() => {
    draftKeyRef.current = tutorialId
      ? `junoro:tutorialDraft:${tutorialId}`
      : "junoro:tutorialDraft:new";
  }, [tutorialId, isOpen]);

  // Element se√ßme ve READY mesajlarƒ±nƒ± dinle
  useEffect(() => {
    const handleMessage = (event) => {
      // Yeni sekme hazƒ±rsa ENABLE mesajƒ±nƒ± tekrar g√∂nder (el sƒ±kƒ±≈üma)
      if (event.data.type === "ELEMENT_SELECTOR_READY") {
        const targetWin = selectorWindowRef.current;
        if (targetWin && !targetWin.closed) {
          try {
            targetWin.postMessage(
              {
                type: "ENABLE_ELEMENT_SELECTOR",
                stepIndex: currentStep,
                returnUrl: window.location.href,
              },
              "*",
            );
          } catch (e) {
            // sessiz ge√ß
          }
        }
        return;
      }

      if (event.data.type === "ELEMENT_SELECTED") {
        const {
          selector,
          navigationSteps,
          registryId,
          stepIndex: incomingStepIndex,
        } = event.data;

        // Eƒüer navigation steps varsa, bunlarƒ± tutorial'a ekle
        if (navigationSteps && navigationSteps.length > 0) {
          // Mevcut adƒ±mdan √∂nce navigation steps'i ekle
          const newSteps = [...tutorialData.steps];
          const currentIndex = newSteps.findIndex(
            (step) => step.id === currentStep,
          );

          // Navigation steps'i tutorial adƒ±mlarƒ±na √ßevir
          const navSteps = navigationSteps.map((navStep, index) => ({
            id: `nav-step-${Date.now()}-${index}`,
            title: `Adƒ±m ${index + 1}: ${navStep.description}`,
            description: `${navStep.action} i≈ülemi yapƒ±n`,
            target: navStep.selector,
            position: "bottom",
            action: navStep.action,
            isNavigationStep: true,
          }));

          // Navigation steps'i mevcut adƒ±mdan √∂nce ekle
          newSteps.splice(currentIndex, 0, ...navSteps);

          setTutorialData((prev) => ({
            ...prev,
            steps: newSteps,
          }));

          setSuccess(
            `${navigationSteps.length} navigasyon adƒ±mƒ± ve hedef element eklendi`,
          );
        } else {
          setSuccess(
            registryId
              ? `ERS ID: ${registryId} se√ßildi`
              : `Element se√ßildi: ${selector}`,
          );
        }

        // Se√ßilen elementi ilgili adƒ±ma ata (gelen stepIndex varsa onu kullan)
        const targetStepIndex =
          typeof incomingStepIndex === "number"
            ? incomingStepIndex
            : currentStep;
        updateStep(targetStepIndex, "target", selector);

        setTimeout(() => setSuccess(""), 3000);
      }
    };

    window.addEventListener("message", handleMessage);
    return () => window.removeEventListener("message", handleMessage);
  }, [currentStep, tutorialData.steps]);

  // Geri d√∂n√º≈üte localStorage √ºzerinden se√ßim bilgisini al ve modal a√ßƒ±ldƒ±ƒüƒ±nda uygula
  useEffect(() => {
    if (!isOpen) return;
    try {
      const raw = localStorage.getItem("junoro:tutorialSelection");
      if (!raw) return;
      const sel = JSON.parse(raw);
      // Son 3 dakika i√ßinde ise ge√ßerli say
      if (sel && sel.selector && Date.now() - sel.timestamp < 3 * 60 * 1000) {
        const idx =
          typeof sel.stepIndex === "number" ? sel.stepIndex : currentStep;
        updateStep(
          idx,
          "target",
          sel.registryId ? `[data-registry="${sel.registryId}"]` : sel.selector,
        );
        setCurrentStep(idx);
        setSuccess(
          sel.registryId
            ? `ERS ID panoya kopyalandƒ±: ${sel.registryId}`
            : `Element se√ßildi: ${sel.selector}`,
        );
        setTimeout(() => setSuccess(""), 3000);
      }
      localStorage.removeItem("junoro:tutorialSelection");
    } catch (e) {
      // sessiz ge√ß
    }
  }, [isOpen]);

  // Modal a√ßƒ±ldƒ±ƒüƒ±nda mevcut draft'ƒ± otomatik geri y√ºkle (Se√ß'e basmadan √∂nceki t√ºm kutucuklar/adƒ±mlar dahil)
  useEffect(() => {
    if (!isOpen || restoredFromDraftRef.current === true) return;
    try {
      const key = draftKeyRef.current;
      if (!key) return;
      const raw = localStorage.getItem(key);
      if (!raw) return;
      const draft = JSON.parse(raw);
      if (draft && draft.tutorialData) {
        setTutorialData(draft.tutorialData);
        if (typeof draft.currentStep === "number") {
          setCurrentStep(draft.currentStep);
        }
        restoredFromDraftRef.current = true;
      }
    } catch (e) {
      // sessiz ge√ß
    }
  }, [isOpen]);

  // Tutorial y√ºkle (d√ºzenleme modu)
  useEffect(() => {
    if (isOpen && tutorialId) {
      // Eƒüer draft restore yapƒ±ldƒ±ysa mevcut y√ºklemeyi atla (geri doldurmayƒ± bozmamak i√ßin)
      if (!restoredFromDraftRef.current) {
        loadExistingTutorial();
      }
    } else if (isOpen && !tutorialId) {
      // Yeni tutorial
      if (!restoredFromDraftRef.current) {
        setTutorialData((prev) => ({
          ...prev,
          id: "",
          title: "",
          description: "",
          type: TUTORIAL_TYPES.PAGE_GUIDE,
          targetPage: null,
          steps: [createEmptyStep()],
        }));
      }
    }
  }, [isOpen, tutorialId]);

  const loadExistingTutorial = async () => {
    try {
      setIsLoading(true);
      const data = await loadTutorial(tutorialId);
      if (data) {
        // Draft restore yapƒ±ldƒ±ysa y√ºklenen verilerle √ºzerine yazma
        if (!restoredFromDraftRef.current) {
          setTutorialData(data);
        }
      }
    } catch (err) {
      setError("Tutorial y√ºklenirken hata olu≈ütu");
    } finally {
      setIsLoading(false);
    }
  };

  const createEmptyStep = () => ({
    id: `step-${Date.now()}`,
    title: "",
    description: "",
    target: "",
    position: "bottom",
    highlightType: "outline",
    content: {
      text: "",
      image: null,
      buttons: [
        {
          text: "Devam",
          action: "next",
          style: "primary",
        },
      ],
    },
  });

  const addStep = () => {
    setTutorialData((prev) => ({
      ...prev,
      steps: [...prev.steps, createEmptyStep()],
    }));
  };

  const removeStep = (index) => {
    if (tutorialData.steps.length > 1) {
      setTutorialData((prev) => ({
        ...prev,
        steps: prev.steps.filter((_, i) => i !== index),
      }));
      if (currentStep >= tutorialData.steps.length - 1) {
        setCurrentStep(Math.max(0, tutorialData.steps.length - 2));
      }
    }
  };

  const updateStep = (index, field, value) => {
    setTutorialData((prev) => ({
      ...prev,
      steps: prev.steps.map((step, i) =>
        i === index ? { ...step, [field]: value } : step,
      ),
    }));
  };

  const updateStepContent = (index, field, value) => {
    setTutorialData((prev) => ({
      ...prev,
      steps: prev.steps.map((step, i) =>
        i === index
          ? {
              ...step,
              content: { ...step.content, [field]: value },
            }
          : step,
      ),
    }));
  };

  // Persist draft on every change while modal is open (so returning won't lose filled fields)
  useEffect(() => {
    if (!isOpen) return;
    try {
      const key = draftKeyRef.current;
      if (!key) return;
      const payload = { tutorialData, currentStep, ts: Date.now() };
      localStorage.setItem(key, JSON.stringify(payload));
    } catch (e) {
      // sessiz ge√ß
    }
  }, [tutorialData, currentStep, isOpen]);

  // Drag and Drop fonksiyonlarƒ±
  const handleDragStart = (e, index) => {
    setDraggedStep(index);
    e.dataTransfer.effectAllowed = "move";
  };

  const handleDragOver = (e) => {
    e.preventDefault();
    e.dataTransfer.dropEffect = "move";
  };

  const handleDrop = (e, dropIndex) => {
    e.preventDefault();

    if (draggedStep === null || draggedStep === dropIndex) {
      setDraggedStep(null);
      return;
    }

    const newSteps = [...tutorialData.steps];
    const draggedItem = newSteps[draggedStep];

    // √ñƒüeyi √ßƒ±kar
    newSteps.splice(draggedStep, 1);

    // Yeni pozisyona ekle
    const insertIndex = draggedStep < dropIndex ? dropIndex - 1 : dropIndex;
    newSteps.splice(insertIndex, 0, draggedItem);

    setTutorialData((prev) => ({
      ...prev,
      steps: newSteps,
    }));

    // Mevcut adƒ±mƒ± g√ºncelle
    if (currentStep === draggedStep) {
      setCurrentStep(insertIndex);
    } else if (draggedStep < currentStep && insertIndex >= currentStep) {
      setCurrentStep(currentStep - 1);
    } else if (draggedStep > currentStep && insertIndex <= currentStep) {
      setCurrentStep(currentStep + 1);
    }

    setDraggedStep(null);
  };

  const handleSave = async () => {
    try {
      setIsLoading(true);
      setError("");

      if (!tutorialData.id || !tutorialData.title) {
        setError("ID ve ba≈ülƒ±k alanlarƒ± zorunludur");
        return;
      }

      await saveTutorial(tutorialData.id, tutorialData);
      // Ba≈üarƒ±lƒ± kayƒ±ttan sonra draft'ƒ± temizle
      try {
        const key = draftKeyRef.current;
        if (key) localStorage.removeItem(key);
      } catch {}
      setSuccess("Tutorial ba≈üarƒ±yla kaydedildi!");
      setTimeout(() => {
        setSuccess("");
        onClose();
      }, 2000);
    } catch (err) {
      setError("Tutorial kaydedilirken hata olu≈ütu");
    } finally {
      setIsLoading(false);
    }
  };

  const handleDelete = async () => {
    if (!tutorialId) return;

    if (window.confirm("Bu tutorial'ƒ± silmek istediƒüinizden emin misiniz?")) {
      try {
        setIsLoading(true);
        await deleteTutorial(tutorialId);
        setSuccess("Tutorial ba≈üarƒ±yla silindi!");
        setTimeout(() => {
          setSuccess("");
          onClose();
        }, 2000);
      } catch (err) {
        setError("Tutorial silinirken hata olu≈ütu");
      } finally {
        setIsLoading(false);
      }
    }
  };

  if (!isOpen) return null;

  // Admin kontrol√º
  if (!isAdmin) {
    return createPortal(
      <div className="fixed inset-0 bg-black/50 flex items-center justify-center z-50">
        <div className="bg-slate-800 rounded-lg p-6 max-w-md mx-4">
          <h3 className="text-xl font-bold text-white mb-4">Yetkisiz Eri≈üim</h3>
          <p className="text-gray-300 mb-4">
            Bu √∂zelliƒüi kullanmak i√ßin admin yetkisine sahip olmanƒ±z gerekiyor.
          </p>
          <button
            onClick={onClose}
            className="w-full bg-red-600 hover:bg-red-700 text-white px-4 py-2 rounded-lg transition-colors"
          >
            Kapat
          </button>
        </div>
      </div>,
      document.body,
    );
  }

  return createPortal(
    <div className="fixed inset-0 bg-black/50 flex items-center justify-center z-50 p-4">
      <div className="bg-slate-800 rounded-lg max-w-4xl w-full max-h-[90vh] overflow-hidden flex flex-col">
        {/* Standart Header */}
        <div className="bg-slate-900/50 border-b border-slate-600/50 backdrop-blur-sm">
          <div className="flex items-center justify-between p-6">
            <div className="flex items-center gap-4">
              {/* Icon ve Ba≈ülƒ±k */}
              <div className="flex items-center gap-3">
                <div className="w-10 h-10 bg-gradient-to-br from-blue-500 to-purple-600 rounded-lg flex items-center justify-center">
                  <span className="text-white text-lg font-bold">‚ùì</span>
                </div>
                <div>
                  <h2 className="text-xl font-bold text-white">
                    {tutorialId ? "Tutorial D√ºzenle" : "Yeni Tutorial Olu≈ütur"}
                  </h2>
                  <p className="text-sm text-gray-400">
                    {tutorialId
                      ? "Mevcut tutorial'ƒ± d√ºzenleyin"
                      : "Yeni bir tutorial olu≈üturun"}
                  </p>
                </div>
              </div>

              {/* Hedef Sayfa Butonu */}
              {tutorialData.id && (
                <button
                  onClick={() => {
                    const targetPage = tutorialData.id.replace("-tutorial", "");
                    const targetUrl =
                      targetPage === "home-page" ? "/" : `/${targetPage}`;
                    window.open(targetUrl, "_blank");
                  }}
                  className="bg-blue-600/20 hover:bg-blue-600/30 border border-blue-500/30 text-blue-300 px-4 py-2 rounded-lg text-sm transition-all duration-200 flex items-center gap-2 hover:scale-105"
                  title="Hedef sayfayƒ± yeni sekmede a√ß"
                >
                  <span className="text-blue-400">üîó</span>
