                  <div className="text-gray-400 text-sm">
                    Versiyon: {parsed.version}
                  </div>
                  {parsed.targetPage && (
                    <div className="text-gray-400 text-sm">
                      Hedef Sayfa: {parsed.targetPage.pageName} (
                      {parsed.targetPage.pagePath})
