                  )}
                </div>
                <div className="text-sm text-gray-400">
                  {existingIds.includes(parsed.id) ? (
                    <span className="text-yellow-300">
                      Uyarƒ±: Bu ID zaten mevcut, kaydedilirse √ºzerine yazƒ±lacak.
                    </span>
                  ) : (
                    <span>Yeni ID</span>
                  )}
                </div>
              </div>
              <div className="mt-3">
                <div className="text-sm text-gray-300">
                  Toplam Adƒ±m: {parsed.steps?.length || 0}
                </div>
                <div className="grid grid-cols-1 md:grid-cols-2 gap-2 mt-2">
                  {parsed.steps.slice(0, 6).map((step, idx) => (
                    <div
                      key={idx}
                      className="bg-slate-700/50 rounded p-2 text-sm"
                    >
                      <div className="text-white truncate">
                        {idx + 1}. {step.title || "Ba≈ülƒ±ksƒ±z"}
                      </div>
                      <div className="text-gray-400 text-xs truncate">
                        Target: {step.target || "‚Äî"}
                      </div>
                    </div>
                  ))}
                  {parsed.steps.length > 6 && (
                    <div className="bg-slate-700/30 rounded p-2 text-sm text-gray-400 flex items-center justify-center">
                      +{parsed.steps.length - 6} daha‚Ä¶
                    </div>
                  )}
                </div>
              </div>
            </div>
          )}

          {/* Actions */}
          <div className="flex items-center justify-end gap-2">
            <button
              onClick={onClose}
              className="bg-slate-700 hover:bg-slate-600 text-white px-4 py-2 rounded-lg"
            >
              ƒ∞ptal
            </button>
            <button
              onClick={proceedToEditor}
              disabled={!parsed}
              className={`px-4 py-2 rounded-lg ${parsed ? "bg-blue-600 hover:bg-blue-700 text-white" : "bg-slate-700 text-gray-400"}`}
            >
              D√ºzenleyiciye Aktar
            </button>
            <button
              onClick={importAndSave}
              disabled={!parsed || isSaving}
              className={`px-4 py-2 rounded-lg ${parsed && !isSaving ? "bg-green-600 hover:bg-green-700 text-white" : "bg-slate-700 text-gray-400"}`}
            >
              {isSaving ? "Kaydediliyor‚Ä¶" : "ƒ∞√ße Aktar ve Kaydet"}
            </button>
          </div>

          {status && (
            <div className="bg-green-600/10 border border-green-500/30 rounded-lg p-3 text-green-300 text-sm">
              {status}
            </div>
          )}
        </div>
      </div>
    </div>,
    document.body,
  );
}

export default TutorialImportModal;

import React, { useState, useEffect, useRef } from "react";
import { tutorialManager, TutorialUtils } from "../../utils/tutorialTypes";

const TutorialOverlay = ({ isVisible, onClose }) => {
  const [currentStep, setCurrentStep] = useState(null);
  const [tutorial, setTutorial] = useState(null);
  const [progress, setProgress] = useState(null);
  const [tooltipPosition, setTooltipPosition] = useState({
    position: "center",
    x: 0,
    y: 0,
  });
  const [highlightStyle, setHighlightStyle] = useState({});
  const overlayRef = useRef(null);
  const tooltipRef = useRef(null);

  useEffect(() => {
    // Register tutorial manager callbacks
    tutorialManager.on({
      onStepShow: (step, stepIndex, tutorialData) => {
        setCurrentStep(step);
        setTutorial(tutorialData);
        setProgress(tutorialManager.getProgress());
        updateHighlight(step);
      },
      onFinish: () => {
        onClose();
      },
    });
  }, [onClose]);

  const updateHighlight = (step) => {
    if (!step.target) {
      setHighlightStyle({});
      setTooltipPosition({
        position: "center",
        x: window.innerWidth / 2,
        y: window.innerHeight / 2,
      });
      return;
    }

    const targetElement = TutorialUtils.getTargetElement(step.target);
    if (!targetElement) {
      console.warn("Target element not found:", step.target);
      return;
    }

    // Scroll to element
    TutorialUtils.scrollToElement(targetElement);

    // Calculate highlight style
    const rect = targetElement.getBoundingClientRect();
    const padding = tutorial?.settings?.highlight?.padding || 8;

    const highlight = {
      position: "fixed",
      top: rect.top - padding,
      left: rect.left - padding,
      width: rect.width + padding * 2,
      height: rect.height + padding * 2,
      borderRadius: tutorial?.settings?.highlight?.borderRadius || 8,
      border: `${tutorial?.settings?.highlight?.borderWidth || 3}px solid ${tutorial?.settings?.highlight?.color || "#8b5cf6"}`,
      backgroundColor:
        step.highlightType === "spotlight"
          ? "rgba(139, 92, 246, 0.1)"
          : "transparent",
      pointerEvents: "none",
      zIndex: 9999,
      transition: "all 0.3s ease",
    };

    setHighlightStyle(highlight);

    // Calculate tooltip position
    const position = TutorialUtils.calculateTooltipPosition(
      targetElement,
      step.position,
    );
    setTooltipPosition(position);
  };

  const handleAction = (action, customAction) => {
    switch (action) {
      case "next":
        tutorialManager.nextStep();
        break;
      case "prev":
        tutorialManager.prevStep();
        break;
      case "skip":
        tutorialManager.skipTutorial();
        break;
      case "finish":
        tutorialManager.finishTutorial();
        break;
      case "custom":
        // Handle custom actions
        console.log("Custom action:", customAction);
        break;
      default:
        break;
    }
  };

  const getTooltipStyle = () => {
    const maxWidth = tutorial?.settings?.modal?.maxWidth || 400;
    const borderRadius = tutorial?.settings?.modal?.borderRadius || 12;
    const backgroundColor =
      tutorial?.settings?.modal?.backgroundColor || "rgba(17, 24, 39, 0.95)";

    let style = {
      position: "fixed",
      maxWidth: `${maxWidth}px`,
      borderRadius: `${borderRadius}px`,
      backgroundColor,
      backdropFilter: "blur(10px)",
      border: "1px solid rgba(139, 92, 246, 0.3)",
      padding: "24px",
      zIndex: 10000,
      transform: "translate(-50%, -50%)",
      transition: "all 0.3s ease",
    };

    if (tooltipPosition.position === "center") {
      style.left = "50%";
      style.top = "50%";
    } else {
      style.left = `${tooltipPosition.x}px`;
      style.top = `${tooltipPosition.y}px`;

      // Adjust transform based on position
      switch (tooltipPosition.position) {
        case "top":
          style.transform = "translate(-50%, -100%)";
          break;
        case "bottom":
          style.transform = "translate(-50%, 0%)";
          break;
        case "left":
          style.transform = "translate(-100%, -50%)";
          break;
        case "right":
          style.transform = "translate(0%, -50%)";
          break;
        default:
          style.transform = "translate(-50%, -50%)";
      }
    }

    return style;
  };

  const getButtonStyle = (buttonStyle) => {
    const baseStyle =
      "px-4 py-2 rounded-lg font-medium transition-all duration-200 ";

    switch (buttonStyle) {
      case "primary":
        return (
          baseStyle +
          "bg-gradient-to-r from-purple-600 to-pink-600 text-white hover:from-purple-700 hover:to-pink-700 hover:scale-105"
        );
      case "secondary":
        return (
          baseStyle +
          "bg-white/10 text-white border border-white/20 hover:bg-white/20"
        );
      case "danger":
        return (
          baseStyle + "bg-red-600 text-white hover:bg-red-700 hover:scale-105"
        );
      default:
        return baseStyle + "bg-gray-600 text-white hover:bg-gray-700";
    }
  };

  if (!isVisible || !currentStep || !tutorial) {
    return null;
  }

  return (
    <div ref={overlayRef} className="tutorial-overlay">
      {/* Overlay Background */}
      <div
        className="fixed inset-0 z-[9998]"
        style={{
          backgroundColor:
            tutorial.settings?.overlay?.color || "rgba(0, 0, 0, 0.7)",
          backdropFilter: tutorial.settings?.overlay?.blur
            ? "blur(2px)"
            : "none",
        }}
        onClick={() =>
          tutorial.settings?.allowSkip && tutorialManager.skipTutorial()
        }
      />

      {/* Highlight */}
      {currentStep.target && currentStep.highlightType !== "none" && (
        <div style={highlightStyle} className="tutorial-highlight" />
      )}

      {/* Tooltip */}
      <div
        ref={tooltipRef}
        style={getTooltipStyle()}
        className="tutorial-tooltip"
      >
        {/* Progress Bar */}
        {tutorial.settings?.showProgress && progress && (
          <div className="mb-4">
            <div className="flex items-center justify-between mb-2">
              <span className="text-sm text-gray-300">
                Adƒ±m {progress.current} / {progress.total}
              </span>
              <span className="text-sm text-purple-400">
                %{progress.percentage}
              </span>
            </div>
            <div className="w-full bg-white/10 rounded-full h-2">
              <div
                className="bg-gradient-to-r from-purple-500 to-pink-500 h-2 rounded-full transition-all duration-300"
                style={{ width: `${progress.percentage}%` }}
              />
            </div>
          </div>
        )}

        {/* Content */}
        <div className="mb-6">
          <h3 className="text-xl font-bold text-white mb-3">
            {currentStep.title}
          </h3>
          <p className="text-gray-300 leading-relaxed">
            {currentStep.content.text}
          </p>

          {/* Optional Image */}
          {currentStep.content.image && (
            <div className="mt-4">
              <img
                src={currentStep.content.image}
                alt={currentStep.title}
                className="w-full rounded-lg"
              />
            </div>
          )}
        </div>

        {/* Action Buttons */}
        <div className="flex gap-3 justify-end">
          {currentStep.content.buttons.map((button, index) => (
            <button
              key={index}
              className={getButtonStyle(button.style)}
              onClick={() => handleAction(button.action, button.customAction)}
            >
              {button.text}
            </button>
          ))}
        </div>

        {/* Skip Button (if allowed) */}
        {tutorial.settings?.allowSkip && (
          <button
            className="absolute top-4 right-4 text-gray-400 hover:text-white transition-colors"
            onClick={() => tutorialManager.skipTutorial()}
            title="Kƒ±lavuzu atla"
          >
            <svg
              className="w-5 h-5"
              fill="none"
              stroke="currentColor"
              viewBox="0 0 24 24"
            >
              <path
                strokeLinecap="round"
                strokeLinejoin="round"
                strokeWidth={2}
                d="M6 18L18 6M6 6l12 12"
              />
            </svg>
          </button>
        )}

        {/* Tooltip Arrow */}
        {tooltipPosition.position !== "center" && (
          <div
            className={`absolute w-3 h-3 bg-gray-800 border border-purple-500/30 transform rotate-45 ${
              tooltipPosition.position === "top"
                ? "bottom-[-6px] left-1/2 -translate-x-1/2"
                : tooltipPosition.position === "bottom"
                  ? "top-[-6px] left-1/2 -translate-x-1/2"
                  : tooltipPosition.position === "left"
                    ? "right-[-6px] top-1/2 -translate-y-1/2"
                    : tooltipPosition.position === "right"
                      ? "left-[-6px] top-1/2 -translate-y-1/2"
                      : ""
            }`}
          />
        )}
      </div>
    </div>
  );
};

export default TutorialOverlay;

import React, { useState, useEffect } from "react";
import { updatesApi } from "../../services/api";

const statuses = [
  { value: "planned", label: "Planlandƒ±" },
  { value: "in_progress", label: "Devam Ediyor" },
  { value: "completed", label: "Tamamlandƒ±" },
  { value: "cancelled", label: "ƒ∞ptal Edildi" },
];

const types = [
  { value: "feature", label: "√ñzellik" },
  { value: "bugfix", label: "Hata D√ºzeltmesi" },
  { value: "improvement", label: "ƒ∞yile≈ütirme" },
  { value: "security", label: "G√ºvenlik" },
  { value: "performance", label: "Performans" },
];

const priorities = [
  { value: "low", label: "D√º≈ü√ºk" },
  { value: "medium", label: "Orta" },
  { value: "high", label: "Y√ºksek" },
  { value: "critical", label: "Kritik" },
];

export default function UpdatesAdmin() {
  const [updates, setUpdates] = useState([]);
  const [loading, setLoading] = useState(true);
  const [saving, setSaving] = useState(false);
  const [error, setError] = useState("");
  const [showForm, setShowForm] = useState(false);
  const [editingUpdate, setEditingUpdate] = useState(null);
  const [expandedUpdate, setExpandedUpdate] = useState(null);
  const [showSubstepForm, setShowSubstepForm] = useState(false);
  const [editingSubstep, setEditingSubstep] = useState(null);
  const [selectedUpdateId, setSelectedUpdateId] = useState(null);
  const [showCreateForm, setShowCreateForm] = useState(false);

  // Form state for new/edit update
  const [formData, setFormData] = useState({
    title: "",
    description: "",
    type: "feature",
    status: "planned",
    priority: "medium",
    version: "",
    progress: 0,
    category: "",
  });

  const [substepFormData, setSubstepFormData] = useState({
    title: "",
    description: "",
    status: "pending",
    progress: 0,
  });

  useEffect(() => {
    fetchUpdates();
  }, []);

  const fetchUpdates = async () => {
    try {
      setLoading(true);
      const response = await updatesApi.getUpdates({ limit: 50 });
      if (response.success) {
        setUpdates(response.data);
      } else {
        setError("G√ºncellemeler y√ºklenemedi");
      }
    } catch (err) {
      console.error("Updates fetch error:", err);
      setError("G√ºncellemeler y√ºklenirken hata olu≈ütu");
    } finally {
      setLoading(false);
    }
  };

  const handleCreateUpdate = async () => {
    try {
      setSaving(true);

      // Validation kontrol√º
      if (!formData.title.trim()) {
        setError("Ba≈ülƒ±k alanƒ± zorunludur");
        return;
      }

      if (!formData.description.trim()) {
        setError("A√ßƒ±klama alanƒ± zorunludur");
        return;
      }

      console.log("Creating update with data:", formData);
      const response = await updatesApi.createUpdate(formData);
      console.log("Create update response:", response);
      if (response.success) {
        await fetchUpdates();
        setShowCreateForm(false);
        resetForm();
      } else {
        setError("G√ºncelleme olu≈üturulamadƒ±");
      }
    } catch (err) {
      console.error("Create update error:", err);
      setError("G√ºncelleme olu≈üturulurken hata olu≈ütu");
    } finally {
      setSaving(false);
    }
  };

  const handleUpdateUpdate = async () => {
    try {
      setSaving(true);
      const response = await updatesApi.updateUpdate(
        editingUpdate.id,
        formData,
      );
      if (response.success) {
        await fetchUpdates();
        setEditingUpdate(null);
        resetForm();
      } else {
        setError("G√ºncelleme d√ºzenlenemedi");
      }
    } catch (err) {
      console.error("Update update error:", err);
      setError("G√ºncelleme d√ºzenlenirken hata olu≈ütu");
    } finally {
      setSaving(false);
    }
  };

  const handleDeleteUpdate = async (updateId) => {
    if (!confirm("Bu g√ºncellemeyi silmek istediƒüinizden emin misiniz?")) return;

    try {
      setSaving(true);
      const response = await updatesApi.deleteUpdate(updateId);
      if (response.success) {
        await fetchUpdates();
      } else {
        setError("G√ºncelleme silinemedi");
      }
    } catch (err) {
      console.error("Delete update error:", err);
      setError("G√ºncelleme silinirken hata olu≈ütu");
    } finally {
      setSaving(false);
    }
  };

  const handleCompleteUpdate = async (updateId) => {
    try {
      setSaving(true);
      const response = await updatesApi.completeUpdate(updateId, {
        releaseDate: new Date().toISOString(),
        notes: "G√ºncelleme tamamlandƒ±",
      });
      if (response.success) {
        await fetchUpdates();
      } else {
        setError("G√ºncelleme tamamlanamadƒ±");
      }
    } catch (err) {
      console.error("Complete update error:", err);
      setError("G√ºncelleme tamamlanƒ±rken hata olu≈ütu");
    } finally {
      setSaving(false);
    }
  };

  const resetForm = () => {
    setFormData({
      title: "",
      version: "",
      description: "",
      type: "feature",
      status: "planned",
      priority: "medium",
      progress: 0,
      category: "",
    });
    setEditingUpdate(null);
    setShowForm(false);
    setShowCreateForm(false);
  };

  const resetSubstepForm = () => {
    setSubstepFormData({
      title: "",
      description: "",
      status: "pending",
      progress: 0,
    });
    setEditingSubstep(null);
    setShowSubstepForm(false);
    setSelectedUpdateId(null);
  };

  const startEdit = (update) => {
    setEditingUpdate(update);
    setFormData({
      title: update.title || "",
      description: update.description || "",
      type: update.type || "feature",
      status: update.status || "planned",
      priority: update.priority || "medium",
      version: update.version || "",
      progress: update.progress || 0,
      category: update.category || "",
    });
  };

  const cancelEdit = () => {
    resetForm();
    setShowCreateForm(false);
  };

  const handleCreateSubstep = async () => {
    if (!substepFormData.title.trim() || !selectedUpdateId) return;

    setSaving(true);
    try {
      const response = await updatesApi.createSubstep(
        selectedUpdateId,
        substepFormData,
      );
      if (response.success) {
        await fetchUpdates();
        resetSubstepForm();
      } else {
        setError(response.error || "Alt adƒ±m olu≈üturulurken hata olu≈ütu");
      }
    } catch (error) {
      console.error("Error creating substep:", error);
      setError("Alt adƒ±m olu≈üturulurken hata olu≈ütu");
    } finally {
      setSaving(false);
    }
  };

  const handleUpdateSubstep = async () => {
    if (!substepFormData.title.trim() || !editingSubstep || !selectedUpdateId)
      return;

    setSaving(true);
    try {
      const response = await updatesApi.updateSubstep(
        selectedUpdateId,
        editingSubstep.id,
        substepFormData,
      );
      if (response.success) {
        await fetchUpdates();
        resetSubstepForm();
      } else {
        setError(response.error || "Alt adƒ±m g√ºncellenirken hata olu≈ütu");
      }
    } catch (error) {
      console.error("Error updating substep:", error);
      setError("Alt adƒ±m g√ºncellenirken hata olu≈ütu");
    } finally {
      setSaving(false);
    }
  };

  const handleDeleteSubstep = async (substepId) => {
    if (!confirm("Bu alt adƒ±mƒ± silmek istediƒüinizden emin misiniz?")) return;

    // Find the update that contains this substep
    const parentUpdate = updates.find(
      (update) =>
        update.substeps &&
        update.substeps.some((substep) => substep.id === substepId),
    );

    if (!parentUpdate) {
      setError("Alt adƒ±mƒ±n ait olduƒüu g√ºncelleme bulunamadƒ±");
      return;
    }

    setSaving(true);
    try {
      const response = await updatesApi.deleteSubstep(
        parentUpdate.id,
        substepId,
      );
      if (response.success) {
        await fetchUpdates();
      } else {
        setError(response.error || "Alt adƒ±m silinirken hata olu≈ütu");
      }
    } catch (error) {
      console.error("Error deleting substep:", error);
      setError("Alt adƒ±m silinirken hata olu≈ütu");
    } finally {
      setSaving(false);
    }
  };

  const startSubstepEdit = (substep, updateId) => {
    setSubstepFormData({
      title: substep.title,
      description: substep.description || "",
      status: substep.status,
      progress: substep.progress || 0,
    });
    setEditingSubstep(substep);
    setSelectedUpdateId(updateId);
    setShowSubstepForm(true);
  };

  const startSubstepCreate = (updateId) => {
    resetSubstepForm();
    setSelectedUpdateId(updateId);
    setShowSubstepForm(true);
  };

  const toggleUpdateExpansion = (updateId) => {
    setExpandedUpdate(expandedUpdate === updateId ? null : updateId);
  };

  if (loading) {
    return (
      <div className="p-6">
        <div className="text-white">G√ºncellemeler y√ºkleniyor...</div>
      </div>
    );
  }

  return (
    <div className="p-6">
      <div className="flex items-center justify-between mb-6">
        <div>
          <h2 className="text-2xl font-bold text-white">
            G√ºncel Geli≈ütirmeler Y√∂netimi
          </h2>
          <p className="text-gray-400">
            Ana sayfada g√∂r√ºnen g√ºncellemeleri buradan y√∂netebilirsiniz.
          </p>
        </div>
        <button
          onClick={() => setShowCreateForm(true)}
          className="px-4 py-2 bg-blue-600 hover:bg-blue-700 text-white rounded-lg transition-colors"
          disabled={saving}
        >
          + Yeni G√ºncelleme
        </button>
      </div>

      {error && (
        <div className="mb-4 p-4 bg-red-500/20 border border-red-500/50 rounded-lg text-red-400">
          {error}
          <button
            onClick={() => setError(null)}
            className="ml-2 text-red-300 hover:text-red-100"
          >
            ‚úï
          </button>
        </div>
      )}

      {/* Create/Edit Form */}
      {(showCreateForm || editingUpdate) && (
        <div className="mb-6 p-6 bg-slate-800/50 border border-slate-700/50 rounded-xl">
          <h3 className="text-lg font-semibold text-white mb-4">
            {editingUpdate ? "G√ºncelleme D√ºzenle" : "Yeni G√ºncelleme Olu≈ütur"}
          </h3>

          <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
            <div>
              <label className="block text-sm text-gray-300 mb-1">
                Ba≈ülƒ±k *
              </label>
