/**
 * @fileoverview LazyImage component unit tests
 * @description Tests LazyImage component functionality and edge cases
 */

import { render, screen, fireEvent, waitFor } from '@testing-library/react';
import { describe, it, expect, beforeEach, afterEach } from 'vitest';
import LazyImage from '../LazyImage.jsx';

// Mock IntersectionObserver
const mockIntersectionObserver = jest.fn();
mockIntersectionObserver.mockReturnValue({
  observe: jest.fn(),
  unobserve: jest.fn(),
  disconnect: jest.fn(),
});
window.IntersectionObserver = mockIntersectionObserver;

describe('LazyImage Component', () => {
  const defaultProps = {
    src: 'https://example.com/image.jpg',
    alt: 'Test image',
    className: 'test-class',
    style: { width: '100px' },
    threshold: 200,
    placeholder: '#f0f0f0'
  };

  beforeEach(() => {
    jest.clearAllMocks();
  });

  afterEach(() => {
    jest.restoreAllMocks();
  });

  describe('Rendering', () => {
    it('should render placeholder initially', () => {
      // Arrange & Act
      render(<LazyImage {...defaultProps} />);
      
      // Assert
      const placeholder = screen.getByRole('img');
      expect(placeholder).toBeInTheDocument();
      expect(placeholder).toHaveStyle({ opacity: '0' });
    });

    it('should render with correct props', () => {
      // Arrange & Act
      render(<LazyImage {...defaultProps} />);
      
      // Assert
      const container = screen.getByRole('img').closest('div');
      expect(container).toHaveClass('lazy-image-container test-class');
      expect(container).toHaveStyle({ width: '100px' });
    });

    it('should have proper accessibility attributes', () => {
      // Arrange & Act
      render(<LazyImage {...defaultProps} />);
      
      // Assert
      const container = screen.getByRole('img').closest('div');
      expect(container).toHaveAttribute('role', 'img');
      expect(container).toHaveAttribute('aria-label', 'Test image');
    });

    it('should use default values when props not provided', () => {
      // Arrange & Act
      render(<LazyImage src="test.jpg" alt="test" />);
      
      // Assert
      const placeholder = screen.getByRole('img');
      expect(placeholder).toHaveStyle({ backgroundColor: '#f3f4f' });
    });
  });

  describe('Intersection Observer', () => {
    it('should observe element on mount', () => {
      // Arrange & Act
      render(<LazyImage {...defaultProps} />);
      
      // Assert
      expect(mockIntersectionObserver).toHaveBeenCalled();
      expect(mockIntersectionObserver.mock.results[0].value.observe).toHaveBeenCalled();
    });

    it('should disconnect observer on unmount', () => {
      // Arrange
      const { unmount } = render(<LazyImage {...defaultProps} />);
      
      // Act
      unmount();
      
      // Assert
      expect(mockIntersectionObserver.mock.results[0].value.disconnect).toHaveBeenCalled();
    });

    it('should load image when intersecting', async () => {
      // Arrange
      let callback;
      mockIntersectionObserver.mockImplementation((cb) => {
        callback = cb;
        return {
          observe: jest.fn(),
          unobserve: jest.fn(),
          disconnect: jest.fn(),
        };
      });
      
      render(<LazyImage {...defaultProps} />);
      
      // Act
      callback([{ isIntersecting: true }]);
      
      // Assert
      await waitFor(() => {
        const img = screen.getByRole('img');
        expect(img).toHaveAttribute('src', 'https://example.com/image.jpg');
      });
    });
  });

  describe('Image Loading', () => {
    it('should show image when loaded', async () => {
      // Arrange
      render(<LazyImage {...defaultProps} />);
      
      // Simulate intersection
      const img = screen.getByRole('img');
      fireEvent.load(img);
      
      // Assert
      await waitFor(() => {
        expect(img).toHaveStyle({ opacity: '1' });
      });
    });

    it('should handle load error', async () => {
      // Arrange
      render(<LazyImage {...defaultProps} />);
      
      // Act
      const img = screen.getByRole('img');
      fireEvent.error(img);
      
      // Assert
      await waitFor(() => {
        const errorElement = screen.getByText('Image failed to load');
        expect(errorElement).toBeInTheDocument();
        expect(errorElement).toHaveClass('text-red-500');
      });
    });

    it('should render error state with custom placeholder', async () => {
      // Arrange
      const propsWithError = { ...defaultProps, placeholder: '#ff0000' };
      render(<LazyImage {...propsWithError} />);
      
      // Act
      const img = screen.getByRole('img');
      fireEvent.error(img);
      
      // Assert
      await waitFor(() => {
        const errorContainer = screen.getByRole('img').closest('div');
        expect(errorContainer).toHaveStyle({ backgroundColor: '#ff0000' });
      });
    });
  });

  describe('Props Handling', () => {
    it('should handle empty alt text', () => {
      // Arrange & Act
      render(<LazyImage src="test.jpg" alt="" />);
      
      // Assert
      const container = screen.getByRole('img').closest('div');
      expect(container).toHaveAttribute('aria-label', '');
    });

    it('should handle custom threshold', () => {
      // Arrange
      let callback;
      mockIntersectionObserver.mockImplementation((cb) => {
        callback = cb;
        return {
          observe: jest.fn(),
          unobserve: jest.fn(),
          disconnect: jest.fn(),
        };
      });
      
      render(<LazyImage {...defaultProps} threshold={100} />);
      
      // Assert
      expect(mockIntersectionObserver).toHaveBeenCalledWith(
        expect.any(Function),
        expect.objectContaining({
          rootMargin: '100px',
          threshold: 0.1,
        })
      );
    });

    it('should handle empty className', () => {
      // Arrange & Act
      render(<LazyImage src="test.jpg" alt="test" className="" />);
      
      // Assert
      const container = screen.getByRole('img').closest('div');
      expect(container).toHaveClass('lazy-image-container');
    });
  });

  describe('Error States', () => {
    it('should handle missing src prop gracefully', () => {
      // Arrange & Act
      expect(() => {
        render(<LazyImage alt="test" />);
      }).not.toThrow();
    });

    it('should handle null src prop', () => {
      // Arrange & Act
      render(<LazyImage src={null} alt="test" />);
      
      // Assert
      const img = screen.getByRole('img');
      expect(img).not.toHaveAttribute('src');
    });

    it('should handle undefined src prop', () => {
      // Arrange & Act
      render(<LazyImage src={undefined} alt="test" />);
      
      // Assert
      const img = screen.getByRole('img');
      expect(img).not.toHaveAttribute('src');
    });
  });

  describe('Performance', () => {
    it('should not create unnecessary re-renders', () => {
      // Arrange
      const { rerender } = render(<LazyImage {...defaultProps} />);
      
      // Act
      rerender(<LazyImage {...defaultProps} />);
      
      // Assert
      expect(mockIntersectionObserver).toHaveBeenCalledTimes(1);
    });

    it('should cleanup properly on unmount', () => {
      // Arrange
      const { unmount } = render(<LazyImage {...defaultProps} />);
      const observer = mockIntersectionObserver.mock.results[0].value;
      
      // Act
      unmount();
      
      // Assert
      expect(observer.disconnect).toHaveBeenCalled();
    });
  });
});

import { createContext, useContext, useState, useEffect } from "react";
import { userService } from "../data/users";

// API Base URL configuration
const API_BASE_URL =
  import.meta.env.VITE_API_URL || "http://localhost:5000/api";

const AuthContext = createContext();

export const useAuth = () => {
  const context = useContext(AuthContext);
  if (!context) {
    throw new Error("useAuth must be used within an AuthProvider");
  }
  return context;
};

export const AuthProvider = ({ children }) => {
  const [user, setUser] = useState(null);
  const [isLoading, setIsLoading] = useState(true);

  // Sayfa y√ºklendiƒüinde localStorage'dan kullanƒ±cƒ± bilgilerini kontrol et
  useEffect(() => {
    const savedUser = localStorage.getItem("arkade_user");
    if (savedUser) {
      try {
        const parsedUser = JSON.parse(savedUser);
        setUser(parsedUser);
        // Backend'den g√ºncel kullanƒ±cƒ± verilerini √ßek
        setTimeout(async () => {
          try {
            const response = await fetch(
              `${API_BASE_URL}/users/${parsedUser.id}`,
            );
            const data = await response.json();
            if (response.ok && data.success) {
              const updatedUser = data.data;
              setUser(updatedUser);
              localStorage.setItem("arkade_user", JSON.stringify(updatedUser));
            }
          } catch (error) {
            console.error("Error refreshing user data on startup:", error);
          }
        }, 100);
      } catch (error) {
        console.error("Kullanƒ±cƒ± bilgileri parse edilemedi:", error);
        localStorage.removeItem("arkade_user");
      }
    }
    setIsLoading(false);
  }, []);

  const login = async (username, password) => {
    try {
      const result = await userService.login(username, password);

      if (result.success) {
        setUser(result.user);
        localStorage.setItem("arkade_user", JSON.stringify(result.user));
        // Login sonrasƒ± kullanƒ±cƒ±yƒ± backend'den tazele (profil g√∂rselleri vb.)
        try {
          await refreshUser();
        } catch {}
        return { success: true };
      } else {
        return { success: false, message: result.message };
      }
    } catch (error) {
      return { success: false, message: "Giri≈ü sƒ±rasƒ±nda bir hata olu≈ütu" };
    }
  };

  const register = async (userData) => {
    try {
      const result = await userService.register(userData);

      if (result.success) {
        // Admin onayƒ± sistemi - kullanƒ±cƒ±yƒ± otomatik login yapmƒ±yoruz
        // setUser(result.user)
        // localStorage.setItem('arkade_user', JSON.stringify(result.user))
        return { success: true, message: result.message };
      } else {
        return { success: false, message: result.message };
      }
    } catch (error) {
      return { success: false, message: "Kayƒ±t sƒ±rasƒ±nda bir hata olu≈ütu" };
    }
  };

  const logout = () => {
    setUser(null);
    localStorage.removeItem("arkade_user");
  };

  const updateUser = (userData) => {
    const updatedUser = { ...user, ...userData };
    setUser(updatedUser);
    localStorage.setItem("arkade_user", JSON.stringify(updatedUser));
  };

  const refreshUser = async () => {
    if (!user?.id) return;

    try {
      const response = await fetch(`${API_BASE_URL}/users/${user.id}`);
      const data = await response.json();

      if (response.ok && data.success) {
        const updatedUser = data.data;
        setUser(updatedUser);
        localStorage.setItem("arkade_user", JSON.stringify(updatedUser));
        return { success: true, user: updatedUser };
      }
    } catch (error) {
      console.error("Error refreshing user data:", error);
    }
    return { success: false };
  };

  const isAdmin = () => {
    return user?.role === "admin";
  };

  const isAuthenticated = () => {
    return !!user;
  };

  const value = {
    user,
    isLoading,
    login,
    register,
    logout,
    updateUser,
    refreshUser,
    isAdmin,
    isAuthenticated,
  };

  return <AuthContext.Provider value={value}>{children}</AuthContext.Provider>;
};

import React, { createContext, useContext, useState, useEffect } from "react";
import { useLocation } from "react-router-dom";

const NavigationContext = createContext();

export const useNavigation = () => {
  const context = useContext(NavigationContext);
  if (!context) {
    throw new Error("useNavigation must be used within a NavigationProvider");
  }
  return context;
};

export const NavigationProvider = ({ children }) => {
  const [navigationHistory, setNavigationHistory] = useState([]);
  const [currentPage, setCurrentPage] = useState(null);
  const location = useLocation();

  // Sayfa bilgileri
  const pageInfo = {
    "/": { title: "Ana Sayfa", icon: "üè†" },
    "/arkade": { title: "Arkade Dashboard", icon: "üéÆ" },
    "/arkade/library": { title: "K√ºt√ºphane", icon: "üìö" },
    "/arkade/session": { title: "Aktif Oturum", icon: "üéÆ" },
    "/gallery": { title: "Galeri", icon: "üñºÔ∏è" },
    "/backlog": { title: "Backlog", icon: "üìã" },
    "/wishlist": { title: "ƒ∞stek Listesi", icon: "‚≠ê" },
    "/stats": { title: "ƒ∞statistikler", icon: "üìà" },
    "/settings": { title: "Ayarlar", icon: "‚öôÔ∏è" },
  };

  useEffect(() => {
    const currentPath = location.pathname;
    const current = pageInfo[currentPath] || {
      title: "Bilinmeyen Sayfa",
      icon: "‚ùì",
    };

    // Eƒüer bu sayfa zaten ge√ßmi≈üte varsa, onu √ßƒ±kar
    const filteredHistory = navigationHistory.filter(
      (page) => page.path !== currentPath,
    );

    // Mevcut sayfayƒ± ge√ßmi≈üe ekle (en son 5 sayfa)
    if (currentPage && currentPage.path !== currentPath) {
      setNavigationHistory([currentPage, ...filteredHistory].slice(0, 5));
    }

    setCurrentPage({ ...current, path: currentPath });
  }, [location.pathname]);

  const goBack = () => {
    if (navigationHistory.length > 0) {
      const lastPage = navigationHistory[0];
      window.history.pushState(null, "", lastPage.path);
      window.location.href = lastPage.path;
    } else {
      // Eƒüer ge√ßmi≈ü yoksa ana sayfaya git
      window.location.href = "/";
    }
  };

  const getLastVisitedPage = () => {
    return navigationHistory.length > 0 ? navigationHistory[0] : null;
  };

  const value = {
    currentPage,
    navigationHistory,
    goBack,
    getLastVisitedPage,
    pageInfo,
  };

  return (
    <NavigationContext.Provider value={value}>
      {children}
    </NavigationContext.Provider>
  );
};

export default NavigationContext;

import { useEffect, useRef, useState } from 'react';

/**
 * Custom hook for managing cleanup operations
 * Helps prevent memory leaks by properly cleaning up resources
 * @param {Function} cleanupFn - Cleanup function to execute
 * @param {Array} deps - Dependency array for effect
 */
function useCleanup(cleanupFn, deps = []) {
  const cleanupRef = useRef(null);

  useEffect(() => {
    // Store previous cleanup function
    const prevCleanup = cleanupRef.current;
    
    // Execute previous cleanup if exists
    if (prevCleanup) {
      prevCleanup();
    }

    // Store new cleanup function
    cleanupRef.current = cleanupFn;

    // Return cleanup function for React's cleanup
    return () => {
      if (cleanupFn) {
        cleanupFn();
      }
      cleanupRef.current = null;
    };
  }, deps);
}

/**
 * Custom hook for managing event listeners with automatic cleanup
 * @param {Element} target - Event target element
 * @param {string} event - Event name
 * @param {Function} handler - Event handler function
 * @param {object} options - Event listener options
 */
function useEventListener(target, event, handler, options = {}) {
  const savedHandler = useRef(handler);
  const savedOptions = useRef(options);

  // Update handler ref if handler changes
  useEffect(() => {
    savedHandler.current = handler;
  }, [handler]);

  // Update options ref if options change
  useEffect(() => {
    savedOptions.current = options;
  }, [options]);

  useEffect(() => {
    // Make sure element supports addEventListener
    if (!target || !target.addEventListener) return;

    // Create event handler that calls latest handler
    const eventListener = (event) => {
      savedHandler.current(event);
    };

    // Add event listener
    target.addEventListener(event, eventListener, savedOptions.current);

    // Return cleanup function
    return () => {
      target.removeEventListener(event, eventListener, savedOptions.current);
    };
  }, [target, event]);
}

/**
 * Custom hook for managing timers with automatic cleanup
 * @param {Function} callback - Timer callback function
 * @param {number} delay - Timer delay in milliseconds
 * @param {Array} deps - Dependency array for effect
 */
function useTimer(callback, delay, deps = []) {
  const timeoutRef = useRef(null);
  const savedCallback = useRef(callback);

  // Update callback ref if callback changes
  useEffect(() => {
    savedCallback.current = callback;
  }, [callback]);

  useEffect(() => {
    // Clear existing timer
    if (timeoutRef.current) {
      clearTimeout(timeoutRef.current);
    }

    // Set new timer if delay is provided
    if (delay !== null && delay !== undefined) {
      timeoutRef.current = setTimeout(() => {
        savedCallback.current();
      }, delay);
    }

    // Return cleanup function
    return () => {
      if (timeoutRef.current) {
        clearTimeout(timeoutRef.current);
        timeoutRef.current = null;
      }
    };
  }, [delay, ...deps]);
}

/**
 * Custom hook for managing intervals with automatic cleanup
 * @param {Function} callback - Interval callback function
 * @param {number} interval - Interval time in milliseconds
 * @param {Array} deps - Dependency array for effect
 */
function useInterval(callback, interval, deps = []) {
  const intervalRef = useRef(null);
  const savedCallback = useRef(callback);

  // Update callback ref if callback changes
  useEffect(() => {
    savedCallback.current = callback;
  }, [callback]);

  useEffect(() => {
    // Clear existing interval
    if (intervalRef.current) {
      clearInterval(intervalRef.current);
    }

    // Set new interval if interval is provided
    if (interval !== null && interval !== undefined) {
      intervalRef.current = setInterval(() => {
        savedCallback.current();
      }, interval);
    }

    // Return cleanup function
    return () => {
      if (intervalRef.current) {
        clearInterval(intervalRef.current);
        intervalRef.current = null;
      }
    };
  }, [interval, ...deps]);
}

/**
 * Custom hook for managing animations with automatic cleanup
 * @param {Function} animationFn - Animation function
 * @param {Array} deps - Dependency array for effect
 */
function useAnimation(animationFn, deps = []) {
  const animationRef = useRef(null);
  const savedAnimationFn = useRef(animationFn);

  // Update animation function ref if it changes
  useEffect(() => {
    savedAnimationFn.current = animationFn;
  }, [animationFn]);

  useEffect(() => {
    // Cancel existing animation
    if (animationRef.current) {
      cancelAnimationFrame(animationRef.current);
    }

    // Start new animation
    const animate = () => {
      savedAnimationFn.current();
      animationRef.current = requestAnimationFrame(animate);
    };

    animationRef.current = requestAnimationFrame(animate);

    // Return cleanup function
    return () => {
      if (animationRef.current) {
        cancelAnimationFrame(animationRef.current);
        animationRef.current = null;
      }
    };
  }, deps);
}

/**
 * Custom hook for managing subscriptions with automatic cleanup
 * @param {Function} subscribe - Subscribe function that returns unsubscribe
 * @param {Array} deps - Dependency array for effect
 * @returns {any} - Current subscription value
 */
function useSubscription(subscribe, deps = []) {
  const subscriptionRef = useRef(null);
  const [value, setValue] = useState(null);

  useEffect(() => {
    // Unsubscribe from previous subscription
    if (subscriptionRef.current) {
      subscriptionRef.current();
    }

    // Subscribe to new subscription
    const unsubscribe = subscribe(setValue);
    subscriptionRef.current = unsubscribe;

    // Return cleanup function
    return () => {
      if (subscriptionRef.current) {
        subscriptionRef.current();
        subscriptionRef.current = null;
      }
    };
  }, deps);

  return value;
}

/**
 * Custom hook for managing external script loading
 * @param {string} src - Script source URL
 * @param {object} options - Script loading options
 * @returns {boolean} - Script loading status
 */
function useExternalScript(src, options = {}) {
  const [isLoaded, setIsLoaded] = useState(false);
  const [error, setError] = useState(null);
  const scriptRef = useRef(null);

  useEffect(() => {
    if (!src) return;

    // Check if script is already loaded
    if (document.querySelector(`script[src="${src}"]`)) {
      setIsLoaded(true);
      return;
    }

    // Create script element
    const script = document.createElement('script');
    script.src = src;
    script.async = options.async !== false;
    script.defer = options.defer === true;

    // Handle script load
    const handleLoad = () => {
      setIsLoaded(true);
      setError(null);
    };

    // Handle script error
    const handleError = () => {
      setError(new Error(`Failed to load script: ${src}`));
      setIsLoaded(false);
    };

    script.addEventListener('load', handleLoad);
    script.addEventListener('error', handleError);

    // Add script to document
    document.head.appendChild(script);
    scriptRef.current = script;

    // Return cleanup function
    return () => {
      script.removeEventListener('load', handleLoad);
      script.removeEventListener('error', handleError);
      
      if (scriptRef.current && scriptRef.current.parentNode) {
        scriptRef.current.parentNode.removeChild(scriptRef.current);
      }
      scriptRef.current = null;
    };
  }, [src, options.async, options.defer]);

  return { isLoaded, error };
}

export {
  useCleanup,
  useEventListener,
  useTimer,
  useInterval,
  useAnimation,
  useSubscription,
  useExternalScript
};

export default useCleanup;

import { useState, useEffect, useCallback } from "react";
import { tutorialManager } from "../utils/tutorialTypes";
import {
  hasVisitedPage,
  markPageAsVisited,
  shouldShowTutorial,
  markTutorialAsCompleted,
  markTutorialAsSkipped,
  loadTutorialSettings,
} from "../utils/pageVisitTracker";

/**
 * Tutorial y√∂netimi i√ßin React hook
 * @param {string} tutorialId - Tutorial ID'si
 * @param {Object} options - Se√ßenekler
 * @returns {Object} Tutorial durumu ve kontrol fonksiyonlarƒ±
 */
export const useTutorial = (tutorialId = null, options = {}) => {
  const { autoStart = false, userId = "default", pageName = null } = options;

  const [isActive, setIsActive] = useState(false);
  const [currentTutorial, setCurrentTutorial] = useState(null);
  const [currentStep, setCurrentStep] = useState(null);
  const [progress, setProgress] = useState(null);
  const [hasSeenTutorial, setHasSeenTutorial] = useState(false);

  // Sayfa ziyaret durumunu kontrol et
  useEffect(() => {
    if (pageName && userId) {
      const visited = hasVisitedPage(userId, pageName);
      setHasSeenTutorial(visited);

      // Sayfa ilk kez ziyaret ediliyorsa ve tutorial g√∂sterilmesi gerekiyorsa
      if (
        !visited &&
        tutorialId &&
        shouldShowTutorial(userId, tutorialId, pageName)
      ) {
        // Sayfayƒ± ziyaret edildi olarak i≈üaretle
        markPageAsVisited(userId, pageName);

        // Tutorial'ƒ± otomatik ba≈ülat
        if (autoStart) {
          setTimeout(() => {
            startTutorial();
          }, 1000); // 1 saniye bekle ki sayfa tam y√ºklensin
        }
      }
    }
  }, [tutorialId, userId, pageName, autoStart]);

  // Tutorial manager callback'lerini kaydet
  useEffect(() => {
    tutorialManager.on({
      onStepShow: (step, stepIndex) => {
        setCurrentStep(step);
        setCurrentTutorial(tutorialManager.getCurrentTutorial());
        setProgress(tutorialManager.getProgress());
        setIsActive(true);
      },
      onFinish: () => {
        setIsActive(false);
        setCurrentStep(null);
        setCurrentTutorial(null);
        setProgress(null);

        // Tutorial tamamlandƒ±ƒüƒ±nƒ± i≈üaretle
        if (tutorialId && userId) {
          markTutorialAsCompleted(userId, tutorialId);
        }
      },
      onSkip: () => {
        setIsActive(false);
        setCurrentStep(null);
        setCurrentTutorial(null);
        setProgress(null);

        // Tutorial atlandƒ±ƒüƒ±nƒ± i≈üaretle
        if (tutorialId && userId) {
          markTutorialAsSkipped(userId, tutorialId);
        }
      },
    });
  }, [tutorialId, userId, startTutorial]);

  /**
   * Tutorial'ƒ± manuel olarak ba≈ülat
   * @param {Object} startOptions - Ba≈ülatma se√ßenekleri
   */
  const startTutorial = useCallback(
    async (startOptions = {}) => {
      if (!tutorialId) {
        console.warn("Tutorial ID belirtilmedi");
        return;
      }

      try {
        await tutorialManager.startTutorial(tutorialId, startOptions);
      } catch (error) {
        console.error("Tutorial ba≈ülatƒ±lƒ±rken hata:", error);
      }
    },
    [tutorialId],
  );

  /**
   * Tutorial'ƒ± durdur
   */
  const stopTutorial = useCallback(() => {
    tutorialManager.skipTutorial();
  }, []);

  /**
   * Sonraki adƒ±ma ge√ß
   */
  const nextStep = useCallback(() => {
    tutorialManager.nextStep();
  }, []);

  /**
   * √ñnceki adƒ±ma ge√ß
   */
  const prevStep = useCallback(() => {
    tutorialManager.prevStep();
  }, []);

  /**
   * Belirli bir adƒ±ma atla
   * @param {number} stepIndex - Hedef adƒ±m indeksi
   */
  const goToStep = useCallback((stepIndex) => {
    tutorialManager.showStep(stepIndex);
  }, []);

  /**
   * Tutorial'ƒ± sƒ±fƒ±rla (g√∂r√ºlmedi olarak i≈üaretle)
   */
  const resetTutorial = useCallback(() => {
    if (!tutorialId || !userId) return;

    // Sayfa ziyaret verilerini sƒ±fƒ±rla
    if (pageName) {
      const visits = JSON.parse(
        localStorage.getItem(`pageVisits_${userId}`) || "{}",
      );
      delete visits[pageName];
      localStorage.setItem(`pageVisits_${userId}`, JSON.stringify(visits));
    }

    // Tutorial ayarlarƒ±nƒ± sƒ±fƒ±rla
    const settings = loadTutorialSettings(userId);
    settings.completedTutorials = settings.completedTutorials.filter(
      (id) => id !== tutorialId,
    );
    settings.skippedTutorials = settings.skippedTutorials.filter(
      (id) => id !== tutorialId,
    );
    localStorage.setItem(
      `userTutorialSettings_${userId}`,
      JSON.stringify(settings),
    );

    setHasSeenTutorial(false);
  }, [tutorialId, userId, pageName]);

  /**
   * Tutorial verilerini ba≈ülatmadan al
   */
  const getTutorialData = useCallback(async () => {
    if (!tutorialId) return null;

    try {
      return await tutorialManager.loadTutorial(tutorialId);
    } catch (error) {
      console.error("Tutorial verileri y√ºklenirken hata:", error);
      return null;
    }
  }, [tutorialId]);

  return {
    // Durum
    isActive,
    currentTutorial,
    currentStep,
    progress,
    hasSeenTutorial,

    // Aksiyonlar
    startTutorial,
    stopTutorial,
    nextStep,
    prevStep,
    goToStep,
    resetTutorial,
    getTutorialData,

    // Yardƒ±mcƒ±lar
    isFirstVisit: !hasSeenTutorial,
    canShowTutorial: !!tutorialId,
    shouldAutoShow:
      tutorialId && userId && pageName
        ? shouldShowTutorial(userId, tutorialId, pageName)
        : false,
  };
};

/**
 * Tutorial admin fonksiyonlarƒ± i√ßin hook
 * @returns {Object} Admin fonksiyonlarƒ±
 */
export const useTutorialAdmin = () => {
  const [isAdmin, setIsAdmin] = useState(false);

  // Admin durumunu kontrol et
  useEffect(() => {
    // localStorage'dan kullanƒ±cƒ± bilgilerini al
    const savedUser = localStorage.getItem("arkade_user");
    if (savedUser) {
      try {
        const user = JSON.parse(savedUser);
        setIsAdmin(user.role === "admin");
      } catch (error) {
        console.error("Kullanƒ±cƒ± bilgileri parse edilemedi:", error);
        setIsAdmin(false);
      }
    } else {
      setIsAdmin(false);
    }
  }, []);

  /**
   * Tutorial verilerini kaydet
   * @param {string} tutorialIdParam - Tutorial tanƒ±mlayƒ±cƒ±sƒ±
   * @param {Object} tutorialData - Tutorial verileri
   */
  const saveTutorial = useCallback(
    async (tutorialIdParam, tutorialData) => {
      if (!isAdmin) {
        throw new Error("Admin eri≈üimi gerekli");
      }

      try {
        // Ger√ßek uygulamada bu backend'e kaydedilir
        // ≈ûimdilik demo i√ßin localStorage kullanƒ±yoruz
        const tutorials = JSON.parse(
          localStorage.getItem("customTutorials") || "{}",
        );
        tutorials[tutorialIdParam] = tutorialData;
        localStorage.setItem("customTutorials", JSON.stringify(tutorials));

        console.log("Tutorial kaydedildi:", tutorialIdParam);
        return true;
      } catch (error) {
        console.error("Tutorial kaydedilirken hata:", error);
        throw error;
      }
    },
    [isAdmin],
  );

  /**
   * Tutorial'ƒ± y√ºkle
   * @param {string} tutorialIdParam - Tutorial tanƒ±mlayƒ±cƒ±sƒ±
   */
  const loadTutorial = useCallback(async (tutorialIdParam) => {
    try {
      const tutorials = JSON.parse(
        localStorage.getItem("customTutorials") || "{}",
      );
      return tutorials[tutorialIdParam] || null;
    } catch (error) {
      console.error("Tutorial y√ºklenirken hata:", error);
      return null;
    }
  }, []);

  /**
   * Tutorial'ƒ± sil
   * @param {string} tutorialIdParam - Tutorial tanƒ±mlayƒ±cƒ±sƒ±
   */
  const deleteTutorial = useCallback(
    async (tutorialIdParam) => {
      if (!isAdmin) {
        throw new Error("Admin eri≈üimi gerekli");
      }

      try {
        const tutorials = JSON.parse(
          localStorage.getItem("customTutorials") || "{}",
        );
        delete tutorials[tutorialIdParam];
        localStorage.setItem("customTutorials", JSON.stringify(tutorials));

        console.log("Tutorial silindi:", tutorialIdParam);
        return true;
      } catch (error) {
        console.error("Tutorial silinirken hata:", error);
        throw error;
      }
    },
    [isAdmin],
  );

  /**
   * T√ºm tutorial'larƒ± listele
   */
  const listTutorials = useCallback(async () => {
    try {
      const tutorials = JSON.parse(
        localStorage.getItem("customTutorials") || "{}",
      );
      return Object.keys(tutorials).map((id) => ({
        id,
        ...tutorials[id],
      }));
    } catch (error) {
      console.error("Tutoriallar listelenirken hata:", error);
      return [];
    }
  }, []);

  return {
    isAdmin,
    saveTutorial,
    loadTutorial,
    deleteTutorial,
    listTutorials,
  };
};

