/**
 * @fileoverview Search utilities unit tests
 * @description Tests search relevance scoring and string similarity functions
 */

import { describe, it, expect, beforeEach } from 'vitest';
import {
  calculateRelevanceScore,
  sortGamesByRelevance,
  formatRelevanceScore,
} from '../searchUtils.js';

describe('Search Utilities', () => {
  const mockGame = {
    id: 1,
    name: 'Test Game',
    summary: 'A great test game for testing purposes',
    genres: [{ name: 'Action' }, { name: 'Adventure' }],
    platforms: [{ name: 'PC' }, { name: 'PlayStation' }],
    rating: 85,
    rating_count: 1500,
    category: 0, // Main game
  };

  const mockDLC = {
    id: 2,
    name: 'Test Game DLC',
    summary: 'Expansion content for test game',
    genres: [{ name: 'Action' }],
    platforms: [{ name: 'PC' }],
    rating: 80,
    rating_count: 500,
    category: 1, // DLC
    parent_game: 1,
  };

  describe('calculateRelevanceScore', () => {
    it('should return 0 for invalid inputs', () => {
      expect(calculateRelevanceScore(null, 'test')).toBe(0);
      expect(calculateRelevanceScore(mockGame, null)).toBe(0);
      expect(calculateRelevanceScore(null, null)).toBe(0);
    });

    it('should give high score for exact name match', () => {
      const score = calculateRelevanceScore(mockGame, 'Test Game');
      expect(score).toBeGreaterThan(80);
    });

    it('should give moderate score for partial name match', () => {
      const score = calculateRelevanceScore(mockGame, 'Test');
      expect(score).toBeGreaterThan(20);
      expect(score).toBeLessThan(40);
    });

    it('should give score for summary match', () => {
      const score = calculateRelevanceScore(mockGame, 'great');
      expect(score).toBeGreaterThan(10);
      expect(score).toBeLessThan(30);
    });

    it('should give score for genre match', () => {
      const score = calculateRelevanceScore(mockGame, 'Action');
      expect(score).toBeGreaterThan(15);
      expect(score).toBeLessThan(25);
    });

    it('should give score for platform match', () => {
      const score = calculateRelevanceScore(mockGame, 'PC');
      expect(score).toBeGreaterThan(5);
      expect(score).toBeLessThan(15);
    });

    it('should penalize DLC games', () => {
      const mainGameScore = calculateRelevanceScore(mockGame, 'Test Game');
      const dlcScore = calculateRelevanceScore(mockDLC, 'Test Game DLC');
      
      expect(dlcScore).toBeLessThan(mainGameScore);
    });

    it('should give popularity bonus', () => {
      const popularGame = {
        ...mockGame,
        rating: 90,
        rating_count: 2000,
      };
      
      const unpopularGame = {
        ...mockGame,
        rating: 60,
        rating_count: 100,
      };
      
      const popularScore = calculateRelevanceScore(popularGame, 'Test Game');
      const unpopularScore = calculateRelevanceScore(unpopularGame, 'Test Game');
      
      expect(popularScore).toBeGreaterThan(unpopularScore);
    });

    it('should handle case insensitive search', () => {
      const score1 = calculateRelevanceScore(mockGame, 'test game');
      const score2 = calculateRelevanceScore(mockGame, 'TEST GAME');
      const score3 = calculateRelevanceScore(mockGame, 'Test Game');
      
      expect(score1).toBe(score2);
      expect(score2).toBe(score3);
    });

    it('should handle whitespace in search', () => {
      const score1 = calculateRelevanceScore(mockGame, '  test game  ');
      const score2 = calculateRelevanceScore(mockGame, 'test game');
      
      expect(score1).toBe(score2);
    });

    it('should return score between 0 and 100', () => {
      const score = calculateRelevanceScore(mockGame, 'Test Game');
      expect(score).toBeGreaterThanOrEqual(0);
      expect(score).toBeLessThanOrEqual(100);
    });
  });

  describe('sortGamesByRelevance', () => {
    const games = [mockGame, mockDLC];

    it('should return original array for invalid inputs', () => {
      expect(sortGamesByRelevance(null, 'test')).toBe(null);
      expect(sortGamesByRelevance(games, null)).toEqual(games);
    });

    it('should sort games by relevance score', () => {
      const sorted = sortGamesByRelevance(games, 'Test Game');
      
      expect(sorted).toHaveLength(2);
      expect(sorted[0].relevanceScore).toBeGreaterThan(sorted[1].relevanceScore);
    });

    it('should filter out zero score games', () => {
      const unrelatedGame = {
        ...mockGame,
        name: 'Completely Different Game',
      };
      
      const allGames = [mockGame, unrelatedGame];
      const sorted = sortGamesByRelevance(allGames, 'Test Game');
      
      expect(sorted).toHaveLength(1);
      expect(sorted[0].name).toBe('Test Game');
    });

    it('should preserve original game properties', () => {
      const sorted = sortGamesByRelevance(games, 'Test Game');
      
      expect(sorted[0]).toHaveProperty('id');
      expect(sorted[0]).toHaveProperty('name');
      expect(sorted[0]).toHaveProperty('summary');
      expect(sorted[0]).toHaveProperty('genres');
      expect(sorted[0]).toHaveProperty('platforms');
    });
  });

  describe('formatRelevanceScore', () => {
    it('should return excellent match for high scores', () => {
      const result = formatRelevanceScore(85);
      
      expect(result.percentage).toBe(85);
      expect(result.label).toBe('M√ºkemmel E≈üle≈üme');
      expect(result.color).toBe('text-green-600');
    });

    it('should return good match for medium-high scores', () => {
      const result = formatRelevanceScore(70);
      
      expect(result.percentage).toBe(70);
      expect(result.label).toBe('ƒ∞yi E≈üle≈üme');
      expect(result.color).toBe('text-blue-600');
    });

    it('should return medium match for medium scores', () => {
      const result = formatRelevanceScore(50);
      
      expect(result.percentage).toBe(50);
      expect(result.label).toBe('Orta E≈üle≈üme');
      expect(result.color).toBe('text-yellow-600');
    });

    it('should return weak match for low scores', () => {
      const result = formatRelevanceScore(30);
      
      expect(result.percentage).toBe(30);
      expect(result.label).toBe('Zayƒ±f E≈üle≈üme');
      expect(result.color).toBe('text-orange-600');
    });

    it('should return poor match for very low scores', () => {
      const result = formatRelevanceScore(10);
      
      expect(result.percentage).toBe(10);
      expect(result.label).toBe('D√º≈ü√ºk E≈üle≈üme');
      expect(result.color).toBe('text-red-600');
    });

    it('should handle boundary values', () => {
      const excellent = formatRelevanceScore(80);
      const good = formatRelevanceScore(60);
      const medium = formatRelevanceScore(40);
      const weak = formatRelevanceScore(20);
      
      expect(excellent.label).toBe('M√ºkemmel E≈üle≈üme');
      expect(good.label).toBe('ƒ∞yi E≈üle≈üme');
      expect(medium.label).toBe('Orta E≈üle≈üme');
      expect(weak.label).toBe('Zayƒ±f E≈üle≈üme');
    });
  });

  describe('Edge Cases', () => {
    it('should handle empty search term', () => {
      const score = calculateRelevanceScore(mockGame, '');
      expect(score).toBe(0);
    });

    it('should handle whitespace-only search term', () => {
      const score = calculateRelevanceScore(mockGame, '   ');
      expect(score).toBe(0);
    });

    it('should handle special characters in search', () => {
      const score = calculateRelevanceScore(mockGame, 'Test@Game#123');
      expect(score).toBeGreaterThanOrEqual(0);
    });

    it('should handle very long search terms', () => {
      const longTerm = 'a'.repeat(1000);
      const score = calculateRelevanceScore(mockGame, longTerm);
      expect(score).toBeGreaterThanOrEqual(0);
      expect(score).toBeLessThanOrEqual(100);
    });

    it('should handle game with missing properties', () => {
      const incompleteGame = {
        id: 1,
        name: 'Test Game',
        // Missing summary, genres, platforms, rating
      };
      
      const score = calculateRelevanceScore(incompleteGame, 'Test Game');
      expect(score).toBeGreaterThan(0);
      expect(score).toBeLessThanOrEqual(100);
    });

    it('should handle game with null properties', () => {
      const gameWithNulls = {
        id: 1,
        name: 'Test Game',
        summary: null,
        genres: null,
        platforms: null,
        rating: null,
        rating_count: null,
      };
      
      const score = calculateRelevanceScore(gameWithNulls, 'Test Game');
      expect(score).toBeGreaterThanOrEqual(0);
      expect(score).toBeLessThanOrEqual(100);
    });
  });

  describe('Performance', () => {
    it('should handle large arrays efficiently', () => {
      const largeGames = Array(1000).fill().map((_, index) => ({
        id: index,
        name: `Game ${index}`,
        summary: `Description for game ${index}`,
        genres: [{ name: 'Action' }],
        platforms: [{ name: 'PC' }],
        rating: 75,
        rating_count: 100,
      }));
      
      const startTime = performance.now();
      const sorted = sortGamesByRelevance(largeGames, 'Game 1');
      const endTime = performance.now();
      
      expect(sorted).toHaveLength(1000);
      expect(endTime - startTime).toBeLessThan(1000); // Should complete in under 1 second
    });

    it('should not modify input array', () => {
      const originalGames = [mockGame, mockDLC];
      const gamesCopy = [...originalGames];
      
      sortGamesByRelevance(originalGames, 'Test Game');
      
      expect(originalGames).toEqual(gamesCopy);
    });
  });

  describe('Integration', () => {
    it('should work end-to-end with realistic data', () => {
      const realisticGames = [
        {
          id: 1,
          name: 'The Legend of Zelda: Breath of the Wild',
          summary: 'An open-world adventure game',
          genres: [{ name: 'Adventure' }, { name: 'Action' }],
          platforms: [{ name: 'Nintendo Switch' }],
          rating: 97,
          rating_count: 15000,
          category: 0,
        },
        {
          id: 2,
          name: 'Zelda DLC: Expansion Pass',
          summary: 'Additional content for Zelda',
          genres: [{ name: 'Adventure' }],
          platforms: [{ name: 'Nintendo Switch' }],
          rating: 85,
          rating_count: 2000,
          category: 1,
          parent_game: 1,
        },
      ];
      
      const sorted = sortGamesByRelevance(realisticGames, 'Zelda');
      
      expect(sorted).toHaveLength(2);
      expect(sorted[0].name).toBe('The Legend of Zelda: Breath of the Wild');
      expect(sorted[0].relevanceScore).toBeGreaterThan(sorted[1].relevanceScore);
    });
  });
});

/**
 * Service Worker registration and management utilities
 * Provides caching strategies and performance optimization
 */

const SW_VERSION = '1.0.0';
const SW_URL = '/sw.js';

/**
 * Check if service workers are supported
 * @returns {boolean} - Service worker support
 */
function isServiceWorkerSupported() {
  return 'serviceWorker' in navigator;
}

/**
 * Register service worker with error handling
 * @param {object} options - Registration options
 * @returns {Promise<object>} - Registration result
 */
async function registerServiceWorker(options = {}) {
  if (!isServiceWorkerSupported()) {
    console.warn('‚ö†Ô∏è Service workers are not supported in this browser');
    return { success: false, error: 'Service workers not supported' };
  }

  try {
    const registration = await navigator.serviceWorker.register(SW_URL, {
      scope: '/',
      ...options
    });

    console.log('‚úÖ Service Worker registered successfully:', registration.scope);

    // Handle updates
    registration.addEventListener('updatefound', () => {
      const newWorker = registration.installing;
      
      newWorker.addEventListener('statechange', () => {
        if (newWorker.state === 'installed' && navigator.serviceWorker.controller) {
          // New version available
          if (confirm('Yeni bir s√ºr√ºm mevcut. G√ºncellemek ister misiniz?')) {
            newWorker.postMessage({ type: 'SKIP_WAITING' });
            window.location.reload();
          }
        }
      });
    });

    return { success: true, registration };
  } catch (error) {
    console.error('‚ùå Service Worker registration failed:', error);
    return { success: false, error: error.message };
  }
}

/**
 * Unregister service worker
 * @returns {Promise<boolean>} - Success status
 */
async function unregisterServiceWorker() {
  if (!isServiceWorkerSupported()) {
    return false;
  }

  try {
    const registration = await navigator.serviceWorker.getRegistration();
    if (registration) {
      const result = await registration.unregister();
      console.log('üóëÔ∏è Service Worker unregistered:', result);
      return result;
    }
    return true;
  } catch (error) {
    console.error('‚ùå Service Worker unregistration failed:', error);
    return false;
  }
}

/**
 * Clear all caches
 * @returns {Promise<boolean>} - Success status
 */
async function clearAllCaches() {
  if (!isServiceWorkerSupported()) {
    return false;
  }

  try {
    const registration = await navigator.serviceWorker.getRegistration();
    if (registration && registration.active) {
      // Send message to service worker to clear caches
      const messageChannel = new MessageChannel();
      
      return new Promise((resolve) => {
        messageChannel.port1.onmessage = (event) => {
          resolve(event.data.success);
        };

        registration.active.postMessage(
          { type: 'CLEAR_CACHE' },
          [messageChannel.port2]
        );

        // Timeout after 5 seconds
        setTimeout(() => resolve(false), 5000);
      });
    }
    return false;
  } catch (error) {
    console.error('‚ùå Cache clearing failed:', error);
    return false;
  }
}

/**
 * Get cache statistics
 * @returns {Promise<object>} - Cache statistics
 */
async function getCacheStats() {
  if (!isServiceWorkerSupported()) {
    return null;
  }

  try {
    const registration = await navigator.serviceWorker.getRegistration();
    if (registration && registration.active) {
      const messageChannel = new MessageChannel();
      
      return new Promise((resolve) => {
        messageChannel.port1.onmessage = (event) => {
          resolve(event.data);
        };

        registration.active.postMessage(
          { type: 'GET_CACHE_STATS' },
          [messageChannel.port2]
        );

        // Timeout after 5 seconds
        setTimeout(() => resolve(null), 5000);
      });
    }
    return null;
  } catch (error) {
    console.error('‚ùå Cache stats failed:', error);
    return null;
  }
}

/**
 * Trigger cache cleanup
 * @returns {Promise<boolean>} - Success status
 */
async function triggerCacheCleanup() {
  if (!isServiceWorkerSupported()) {
    return false;
  }

  try {
    const registration = await navigator.serviceWorker.getRegistration();
    if (registration && registration.active) {
      registration.active.postMessage({ type: 'CACHE_CLEANUP' });
      return true;
    }
    return false;
  } catch (error) {
    console.error('‚ùå Cache cleanup failed:', error);
    return false;
  }
}

/**
 * Check if service worker is active
 * @returns {boolean} - Service worker status
 */
function isServiceWorkerActive() {
  return isServiceWorkerSupported() && 
         navigator.serviceWorker.controller !== null;
}

/**
 * Get service worker version
 * @returns {Promise<string|null>} - Service worker version
 */
async function getServiceWorkerVersion() {
  if (!isServiceWorkerSupported()) {
    return null;
  }

  try {
    const registration = await navigator.serviceWorker.getRegistration();
    if (registration && registration.active) {
      return SW_VERSION;
    }
    return null;
  } catch (error) {
    console.error('‚ùå Version check failed:', error);
    return null;
  }
}

/**
 * Initialize service worker with best practices
 * @param {object} options - Initialization options
 * @returns {Promise<object>} - Initialization result
 */
async function initializeServiceWorker(options = {}) {
  const {
    autoRegister = true,
    showUpdatePrompt = true,
    clearCacheOnUpdate = false
  } = options;

  if (!isServiceWorkerSupported()) {
    return { success: false, error: 'Service workers not supported' };
  }

  // Wait for page to load
  if (document.readyState === 'loading') {
    await new Promise(resolve => {
      document.addEventListener('DOMContentLoaded', resolve);
    });
  }

  if (autoRegister) {
    const result = await registerServiceWorker();
    
    if (result.success && showUpdatePrompt) {
      // Set up update checking
      setInterval(async () => {
        try {
          if (result.registration) {
            await result.registration.update();
          }
        } catch (error) {
          // Ignore update errors
        }
      }, 60 * 60 * 1000); // Check every hour
    }

    return result;
  }

  return { success: true, message: 'Service worker support detected' };
}

/**
 * Service worker manager class for advanced usage
 */
class ServiceWorkerManager {
  constructor(options = {}) {
    this.options = {
      autoRegister: true,
      showUpdatePrompt: true,
      clearCacheOnUpdate: false,
      ...options
    };
    this.registration = null;
    this.isInitialized = false;
  }

  /**
   * Initialize the service worker
   * @returns {Promise<object>} - Initialization result
   */
  async init() {
    if (this.isInitialized) {
      return { success: true, message: 'Already initialized' };
    }

    const result = await initializeServiceWorker(this.options);
    
    if (result.success) {
      this.isInitialized = true;
      this.registration = await navigator.serviceWorker.getRegistration();
    }

    return result;
  }

  /**
   * Clear all caches
   * @returns {Promise<boolean>} - Success status
   */
  async clearCache() {
    return await clearAllCaches();
  }

  /**
   * Get cache statistics
   * @returns {Promise<object|null>} - Cache statistics
   */
  async getStats() {
    return await getCacheStats();
  }

  /**
   * Trigger cache cleanup
   * @returns {Promise<boolean>} - Success status
   */
  async cleanup() {
    return await triggerCacheCleanup();
  }

  /**
   * Check if service worker is active
   * @returns {boolean} - Service worker status
   */
  isActive() {
    return isServiceWorkerActive();
  }

  /**
   * Get service worker version
   * @returns {Promise<string|null>} - Service worker version
   */
  async getVersion() {
    return await getServiceWorkerVersion();
  }
}

export {
  isServiceWorkerSupported,
  registerServiceWorker,
  unregisterServiceWorker,
  clearAllCaches,
  getCacheStats,
  triggerCacheCleanup,
  isServiceWorkerActive,
  getServiceWorkerVersion,
  initializeServiceWorker,
  ServiceWorkerManager
};

export default ServiceWorkerManager;

// Tutorial Import Parser
// .txt dosyasƒ±ndan tutorial verisini i√ßeri aktarmak i√ßin yardƒ±mcƒ± fonksiyonlar
// Desteklenen formatlar:
// 1) JSON: Dosya i√ßeriƒüi doƒürudan tutorial ≈üemasƒ±na uygun JSON
// 2) DSL: B√∂l√ºmlere ayrƒ±lmƒ±≈ü satƒ±r tabanlƒ± format
//    [TUTORIAL], [TARGET_PAGE], [SETTINGS], [STEP]
//    key = value satƒ±rlarƒ±, content.* ve overlay.*, highlight.*, modal.* i√ßin noktalƒ± alanlar desteklenir

import { TUTORIAL_TYPES, PAGE_CATEGORIES } from "./tutorialTypes";

const defaultSettings = {
  autoStart: true,
  showProgress: true,
  allowSkip: true,
  overlay: {
    color: "rgba(0, 0, 0, 0.7)",
    blur: true,
  },
  highlight: {
    color: "#8b5cf6",
    borderWidth: 3,
    borderRadius: 8,
    padding: 8,
  },
  modal: {
    maxWidth: 400,
    borderRadius: 12,
    backgroundColor: "rgba(17, 24, 39, 0.95)",
    textColor: "#ffffff",
  },
};

const defaultStep = () => ({
  id: `step-${Date.now()}`,
  title: "",
  description: "",
  target: "",
  position: "bottom",
  highlightType: "outline",
  content: {
    text: "",
    image: null,
    buttons: [{ text: "Devam", action: "next", style: "primary" }],
  },
});

const toBoolean = (val) => {
  if (typeof val === "boolean") return val;
  if (typeof val !== "string") return false;
  return val.trim().toLowerCase() === "true";
};

const toNumber = (val) => {
  const n = Number(val);
  return Number.isNaN(n) ? undefined : n;
};

const slugify = (str) => {
  return String(str || "")
    .toLowerCase()
    .replace(/[^a-z0-9\s-]/g, "")
    .trim()
    .replace(/\s+/g, "-");
};

const resolvePagePath = (categoryId, pageId) => {
  const category = Object.values(PAGE_CATEGORIES).find(
    (c) => c.id === categoryId,
  );
  const page = category?.pages.find((p) => p.id === pageId);
  return page ? { pageName: page.name, pagePath: page.path } : null;
};

const normalizeTarget = (value) => {
  if (!value) return "";
  const v = String(value).trim();
  // ERS id desteƒüi: registryId:XYZ veya sadece XYZ
  if (v.startsWith("registryId:")) {
    const id = v.split(":")[1];
    return `[data-registry="${id}"]`;
  }
  if (v.startsWith("data-registry:")) {
    const id = v.split(":")[1];
    return `[data-registry="${id}"]`;
  }
  // [data-registry="XYZ"] veya #id/.class gibi direkt selector ise aynen d√∂nd√ºr
  return v;
};

const mapType = (value) => {
  const v = String(value || "").toLowerCase();
  if (v === "page_guide" || v === "page-guide")
    return TUTORIAL_TYPES.PAGE_GUIDE;
  if (v === "sub_guide" || v === "sub-guide") return TUTORIAL_TYPES.SUB_GUIDE;
  return TUTORIAL_TYPES.PAGE_GUIDE;
};

function tryParseJSON(text) {
  try {
    const obj = JSON.parse(text);
    return obj;
  } catch (e) {
    return null;
  }
}

// Basit DSL parser
function parseDSL(text) {
  const lines = text.split(/\r?\n/);
  let section = null;
  const tutorial = {
    id: "",
    title: "",
    description: "",
    version: "1.0.0",
    type: TUTORIAL_TYPES.PAGE_GUIDE,
    targetPage: null,
    steps: [],
    settings: JSON.parse(JSON.stringify(defaultSettings)),
  };

  let currentStep = null;

  const assignNested = (obj, keyPath, rawValue) => {
    const keys = keyPath.split(".");
    let targetObj = obj;
    for (let i = 0; i < keys.length - 1; i++) {
      const k = keys[i];
      if (!targetObj[k] || typeof targetObj[k] !== "object") {
        targetObj[k] = {};
      }
      targetObj = targetObj[k];
    }
    const lastKey = keys[keys.length - 1];
    let value = rawValue;
    // Tip d√∂n√º≈ü√ºmleri
    if (/^(true|false)$/i.test(rawValue)) {
      value = toBoolean(rawValue);
    } else if (/^\d+$/.test(rawValue)) {
      const num = toNumber(rawValue);
      if (num !== undefined) value = num;
    } else if (
      (rawValue && rawValue.trim().startsWith("{")) ||
      rawValue.trim().startsWith("[")
    ) {
      // JSON string
      try {
        value = JSON.parse(rawValue);
      } catch {}
    }
    targetObj[lastKey] = value;
  };

  for (const rawLine of lines) {
    const line = rawLine.trim();
    if (!line || line.startsWith("//") || line.startsWith("#")) continue;

    // Section deƒüi≈üimi
    const m = line.match(/^\[(.+?)\]$/);
    if (m) {
      section = m[1].toUpperCase();
      if (section === "STEP") {
        currentStep = defaultStep();
        tutorial.steps.push(currentStep);
      }
      continue;
    }

    // key = value
    const kv = line.match(/^(.*?)\s*=\s*(.+)$/);
    if (!kv) continue;
    const key = kv[1].trim();
    const value = kv[2].trim();

    switch (section) {
      case "TUTORIAL": {
        if (key === "id") tutorial.id = value;
        else if (key === "title") tutorial.title = value;
        else if (key === "description") tutorial.description = value;
        else if (key === "version") tutorial.version = value;
        else if (key === "type") tutorial.type = mapType(value);
        break;
      }
      case "TARGET_PAGE": {
        if (!tutorial.targetPage) tutorial.targetPage = {};
        if (key === "categoryId") tutorial.targetPage.categoryId = value;
        else if (key === "pageId") tutorial.targetPage.pageId = value;
        else if (key === "pagePath") tutorial.targetPage.pagePath = value;
        else if (key === "pageName") tutorial.targetPage.pageName = value;
        break;
      }
      case "SETTINGS": {
        if (
          key.startsWith("overlay.") ||
          key.startsWith("highlight.") ||
          key.startsWith("modal.")
        ) {
          assignNested(tutorial.settings, key, value);
        } else {
          if (key === "autoStart")
            tutorial.settings.autoStart = toBoolean(value);
          else if (key === "showProgress")
            tutorial.settings.showProgress = toBoolean(value);
          else if (key === "allowSkip")
            tutorial.settings.allowSkip = toBoolean(value);
        }
        break;
      }
      case "STEP": {
        if (!currentStep) {
          currentStep = defaultStep();
          tutorial.steps.push(currentStep);
        }
        if (key === "id") currentStep.id = value;
        else if (key === "title") currentStep.title = value;
        else if (key === "description") currentStep.description = value;
        else if (key === "target") currentStep.target = normalizeTarget(value);
        else if (key === "position") currentStep.position = value;
        else if (key === "highlightType") currentStep.highlightType = value;
        else if (key.startsWith("content."))
          assignNested(currentStep, key, value);
        break;
      }
      default:
        // Bo≈ü veya tanƒ±msƒ±z b√∂l√ºm
        break;
    }
  }

  // targetPage i√ßin path ve name'i doldur
  if (tutorial.targetPage?.categoryId && tutorial.targetPage?.pageId) {
    const resolved = resolvePagePath(
      tutorial.targetPage.categoryId,
      tutorial.targetPage.pageId,
    );
    if (resolved) {
      tutorial.targetPage.pageName =
        tutorial.targetPage.pageName || resolved.pageName;
      tutorial.targetPage.pagePath =
        tutorial.targetPage.pagePath || resolved.pagePath;
    }
  }

  return tutorial;
}

function normalizeTutorial(obj) {
  const tutorial = {
    id:
      obj.id ||
      (obj.title ? `${slugify(obj.title)}-tutorial` : `tutorial-${Date.now()}`),
    title: obj.title || "Yeni Tutorial",
    description: obj.description || "",
    version: obj.version || "1.0.0",
    type: mapType(obj.type),
    targetPage: obj.targetPage || null,
    steps:
      Array.isArray(obj.steps) && obj.steps.length > 0
        ? obj.steps.map((s, i) => ({
            id: s.id || `step-${Date.now()}-${i}`,
            title: s.title || `Adƒ±m ${i + 1}`,
            description: s.description || "",
            target: normalizeTarget(s.target || ""),
            position: s.position || "bottom",
            highlightType: s.highlightType || "outline",
            content: {
              text: s.content?.text || "",
              image: s.content?.image || null,
              buttons:
                Array.isArray(s.content?.buttons) &&
                s.content.buttons.length > 0
                  ? s.content.buttons
                  : [{ text: "Devam", action: "next", style: "primary" }],
            },
          }))
        : [defaultStep()],
    settings: { ...defaultSettings, ...(obj.settings || {}) },
  };

  if (tutorial.targetPage?.categoryId && tutorial.targetPage?.pageId) {
    const resolved = resolvePagePath(
      tutorial.targetPage.categoryId,
      tutorial.targetPage.pageId,
    );
    if (resolved) {
      tutorial.targetPage.pageName =
        tutorial.targetPage.pageName || resolved.pageName;
      tutorial.targetPage.pagePath =
        tutorial.targetPage.pagePath || resolved.pagePath;
    }
  }

  return tutorial;
}

export function parseTutorialText(text) {
  const errors = [];
  let rawObj = tryParseJSON(text);
  if (!rawObj) {
    try {
      rawObj = parseDSL(text);
    } catch (e) {
      errors.push("DSL parse edilirken hata olu≈ütu: " + e.message);
    }
  }

  if (!rawObj) {
    return {
      tutorial: null,
      errors: errors.length
        ? errors
        : ["Ge√ßersiz dosya formatƒ±. JSON veya DSL kullanƒ±n."],
    };
  }

  try {
    const tutorial = normalizeTutorial(rawObj);
    // Basit doƒürulamalar
    if (!tutorial.id) errors.push("ID √ºretilemedi");
    if (!tutorial.title) errors.push("Ba≈ülƒ±k eksik");
    if (!Array.isArray(tutorial.steps) || tutorial.steps.length === 0)
      errors.push("En az bir adƒ±m olmalƒ±");

    return { tutorial, errors };
  } catch (e) {
    errors.push("Normalize edilirken hata olu≈ütu: " + e.message);
    return { tutorial: null, errors };
  }
}

